import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { coursesAPI, studentsAPI } from '../services/api';
import { useCourseSearch } from '../hooks/useCourseSearch';
import SearchFilter from '../components/SearchFilter';
import CourseCard from '../components/CourseCard';
import Pagination from '../components/Pagination';
import EmptyState from '../components/EmptyState';
import LoadingSpinner, { LoadingCard } from '../components/LoadingSpinner';
import { Plus, X } from 'lucide-react';

const CoursesPage = () => {
  const { user } = useAuth();
  
  // Use the custom hook for search and data management
  const {
    courses,
    loading,
    error,
    searchTerm,
    filters,
    pagination,
    handleSearchChange,
    handleFilterChange,
    handlePageChange,
    clearSearch,
    refreshCourses,
    hasResults,
    isEmpty,
    hasFilters
  } = useCourseSearch();
  // Modal state
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [modalType, setModalType] = useState('create'); // 'create', 'edit', 'delete'
  const [modalLoading, setModalLoading] = useState(false);

  useEffect(() => {
    fetchCourses();
  }, [searchTerm, filters, pagination.currentPage]);

  const fetchCourses = async () => {
    try {
      setLoading(true);
      const params = {
        page: pagination.currentPage,
        search: searchTerm,
        ...filters,
      };

      console.log("🔍 Fetching courses with params:", params);
      const response = await coursesAPI.list(params);
      console.log("📊 API Response:", response.data);

      setCourses(response.data.results || response.data || []);

      if (response.data.count) {
        setPagination((prev) => ({
          ...prev,
          totalCount: response.data.count,
          totalPages: Math.ceil(response.data.count / 10),
        }));
      }
    } catch (error) {
      console.error("❌ Error fetching courses:", error);
      console.error("Error details:", error.response?.data);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateCourse = () => {
    setModalType("create");
    setSelectedCourse(null);
    setShowModal(true);
  };

  const handleEditCourse = (course) => {
    setModalType("edit");
    setSelectedCourse(course);
    setShowModal(true);
  };

  const handleDeleteCourse = (course) => {
    setModalType("delete");
    setSelectedCourse(course);
    setShowModal(true);
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">
            Courses Management
          </h1>
          <p className="mt-1 text-sm text-gray-500">
            Manage all courses in the system ({courses.length} course
            {courses.length !== 1 ? "s" : ""} found)
          </p>
        </div>
        {user?.role === "admin" && (
          <button
            onClick={handleCreateCourse}
            className="btn-primary flex items-center"
          >
            <Plus className="h-4 w-4 mr-2" />
            Add Course
          </button>
        )}
      </div>

      {/* Search and Filters */}
      <div className="bg-white shadow rounded-lg p-6">
        <div className="grid grid-cols-1 gap-4 sm:grid-cols-3">
          {/* Search */}
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
            <input
              type="text"
              placeholder="Search courses by name, code, or description..."
              value={searchTerm}
              onChange={(e) => {
                console.log("🔎 Search term changed to:", e.target.value);
                setSearchTerm(e.target.value);
              }}
              className="input-field pl-10"
            />
          </div>

          {/* Status Filter */}
          <select
            value={filters.is_active}
            onChange={(e) =>
              setFilters((prev) => ({ ...prev, is_active: e.target.value }))
            }
            className="input-field"
          >
            <option value="">All Status</option>
            <option value="true">Active</option>
            <option value="false">Inactive</option>
          </select>

          {/* Actions */}
          <div className="flex space-x-2">
            <button
              onClick={fetchCourses}
              className="btn-secondary flex items-center flex-1"
            >
              <Filter className="h-4 w-4 mr-2" />
              Refresh
            </button>
            {user?.role === "admin" && (
              <button className="btn-secondary flex items-center">
                <Download className="h-4 w-4" />
              </button>
            )}
          </div>
        </div>
      </div>

      {/* Courses Grid */}
      <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3">
        {courses.length > 0 ? (
          courses.map((course) => (
            <div
              key={course.course_id}
              className="bg-white shadow rounded-lg overflow-hidden hover:shadow-lg transition-shadow duration-300"
            >
              <div className="px-6 py-4">
                {/* Course Header */}
                <div className="flex items-start justify-between mb-3">
                  <div className="flex-1">
                    <h3 className="text-lg font-semibold text-gray-900 mb-1">
                      {course.course_name}
                    </h3>
                    <p className="text-sm text-gray-600 font-mono">
                      {course.course_code}
                    </p>
                  </div>
                  <span
                    className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                      course.is_active
                        ? "bg-green-100 text-green-800"
                        : "bg-red-100 text-red-800"
                    }`}
                  >
                    {course.is_active ? "Active" : "Inactive"}
                  </span>
                </div>

                {/* Course Details */}
                <div className="space-y-3 mb-4">
                  {course.description && (
                    <div>
                      <span className="text-xs font-medium text-gray-700 uppercase tracking-wide">
                        Description
                      </span>
                      <p
                        className="text-sm text-gray-600 mt-1"
                        style={{
                          display: "-webkit-box",
                          WebkitLineClamp: 3,
                          WebkitBoxOrient: "vertical",
                          overflow: "hidden",
                          maxHeight: "4.5em",
                        }}
                      >
                        {course.description}
                      </p>
                    </div>
                  )}

                  <div className="grid grid-cols-2 gap-3">
                    <div>
                      <span className="text-xs font-medium text-gray-700 uppercase tracking-wide">
                        Credits
                      </span>
                      <p className="text-sm text-gray-900 font-semibold">
                        {course.credits || "N/A"}
                      </p>
                    </div>
                    <div>
                      <span className="text-xs font-medium text-gray-700 uppercase tracking-wide">
                        Duration
                      </span>
                      <p className="text-sm text-gray-900 font-semibold">
                        {course.course_duration
                          ? `${course.course_duration} months`
                          : "N/A"}
                      </p>
                    </div>
                  </div>

                  {course.prerequisites && (
                    <div>
                      <span className="text-xs font-medium text-gray-700 uppercase tracking-wide">
                        Prerequisites
                      </span>
                      <p
                        className="text-sm text-gray-600 mt-1"
                        style={{
                          display: "-webkit-box",
                          WebkitLineClamp: 2,
                          WebkitBoxOrient: "vertical",
                          overflow: "hidden",
                          maxHeight: "3em",
                        }}
                      >
                        {course.prerequisites}
                      </p>
                    </div>
                  )}

                  <div className="flex justify-between items-center">
                    <div>
                      <span className="text-xs font-medium text-gray-700 uppercase tracking-wide">
                        Students
                      </span>
                      <p className="text-sm text-gray-900 font-semibold">
                        {course.enrolled_students_count || 0}
                      </p>
                    </div>
                    {course.created_at && (
                      <div className="text-right">
                        <span className="text-xs font-medium text-gray-700 uppercase tracking-wide">
                          Created
                        </span>
                        <p className="text-xs text-gray-600">
                          {new Date(course.created_at).toLocaleDateString()}
                        </p>
                      </div>
                    )}
                  </div>
                </div>

                {/* Action Buttons */}
                {user?.role === "admin" && (
                  <div className="flex space-x-2 pt-4 border-t border-gray-100">
                    <button
                      onClick={() => handleEditCourse(course)}
                      className="flex-1 bg-blue-50 text-blue-700 hover:bg-blue-100 px-3 py-2 rounded-lg text-sm font-medium transition-colors flex items-center justify-center"
                    >
                      <Edit className="h-4 w-4 mr-1" />
                      Edit
                    </button>
                    <button
                      onClick={() => handleDeleteCourse(course)}
                      className="flex-1 bg-red-50 text-red-700 hover:bg-red-100 px-3 py-2 rounded-lg text-sm font-medium transition-colors flex items-center justify-center"
                    >
                      <Trash2 className="h-4 w-4 mr-1" />
                      Delete
                    </button>
                  </div>
                )}
              </div>
            </div>
          ))
        ) : (
          <div className="col-span-full flex flex-col items-center justify-center py-12">
            <BookOpen className="h-12 w-12 text-gray-400 mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">
              No courses found
            </h3>
            <p className="text-gray-500 text-center max-w-md">
              {searchTerm || filters.is_active
                ? "Try adjusting your search criteria or filters."
                : "No courses are available at the moment."}
            </p>
          </div>
        )}
      </div>

      {/* Pagination */}
      {pagination.totalPages > 1 && (
        <div className="flex items-center justify-between">
          <div className="flex-1 flex justify-between sm:hidden">
            <button
              onClick={() =>
                setPagination((prev) => ({
                  ...prev,
                  currentPage: Math.max(prev.currentPage - 1, 1),
                }))
              }
              disabled={pagination.currentPage === 1}
              className="btn-secondary"
            >
              Previous
            </button>
            <button
              onClick={() =>
                setPagination((prev) => ({
                  ...prev,
                  currentPage: Math.min(prev.currentPage + 1, prev.totalPages),
                }))
              }
              disabled={pagination.currentPage === pagination.totalPages}
              className="btn-secondary"
            >
              Next
            </button>
          </div>
          <div className="hidden sm:flex-1 sm:flex sm:items-center sm:justify-between">
            <div>
              <p className="text-sm text-gray-700">
                Showing{" "}
                <span className="font-medium">
                  {(pagination.currentPage - 1) * 10 + 1}
                </span>{" "}
                to{" "}
                <span className="font-medium">
                  {Math.min(pagination.currentPage * 10, pagination.totalCount)}
                </span>{" "}
                of <span className="font-medium">{pagination.totalCount}</span>{" "}
                results
              </p>
            </div>
            <div>
              <nav
                className="relative z-0 inline-flex rounded-md shadow-sm -space-x-px"
                aria-label="Pagination"
              >
                {Array.from(
                  { length: pagination.totalPages },
                  (_, i) => i + 1
                ).map((page) => (
                  <button
                    key={page}
                    onClick={() =>
                      setPagination((prev) => ({ ...prev, currentPage: page }))
                    }
                    className={`relative inline-flex items-center px-4 py-2 border text-sm font-medium ${
                      page === pagination.currentPage
                        ? "z-10 bg-primary-50 border-primary-500 text-primary-600"
                        : "bg-white border-gray-300 text-gray-500 hover:bg-gray-50"
                    }`}
                  >
                    {page}
                  </button>
                ))}
              </nav>
            </div>
          </div>
        </div>
      )}

      {/* Course Modal */}
      {showModal && (
        <CourseModal
          type={modalType}
          course={selectedCourse}
          onClose={() => setShowModal(false)}
          onSuccess={() => {
            setShowModal(false);
            fetchCourses();
          }}
        />
      )}
    </div>
  );
};

// Course Modal Component
const CourseModal = ({ type, course, onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    course_name: course?.course_name || "",
    course_code: course?.course_code || "",
    description: course?.description || "",
    prerequisites: course?.prerequisites || "",
    course_duration: course?.course_duration || "",
    credits: course?.credits || "",
    is_active: course?.is_active ?? true,
  });
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setErrors({});

    try {
      if (type === "create") {
        await coursesAPI.create(formData);
      } else if (type === "edit") {
        await coursesAPI.update(course.course_id, formData);
      } else if (type === "delete") {
        await coursesAPI.delete(course.course_id);
      }
      onSuccess();
    } catch (error) {
      if (error.response?.data) {
        setErrors(error.response.data);
      }
    } finally {
      setLoading(false);
    }
  };

  if (type === "delete") {
    return (
      <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center z-50">
        <div className="bg-white rounded-lg p-6 max-w-md w-full mx-4">
          <div className="flex items-center mb-4">
            <AlertCircle className="h-6 w-6 text-red-600 mr-3" />
            <h3 className="text-lg font-medium text-gray-900">Delete Course</h3>
          </div>
          <p className="text-sm text-gray-500 mb-4">
            Are you sure you want to delete "{course?.course_name}"? This action
            cannot be undone.
          </p>
          <div className="flex space-x-3">
            <button
              onClick={onClose}
              className="btn-secondary flex-1"
              disabled={loading}
            >
              Cancel
            </button>
            <button
              onClick={handleSubmit}
              className="btn-danger flex-1"
              disabled={loading}
            >
              {loading ? "Deleting..." : "Delete"}
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 max-w-2xl w-full mx-4 max-h-screen overflow-y-auto">
        <div className="flex items-center justify-between mb-6">
          <h3 className="text-lg font-medium text-gray-900">
            {type === "create" ? "Create New Course" : "Edit Course"}
          </h3>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600 transition-colors"
          >
            <X className="h-6 w-6" />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Course Name *
              </label>
              <input
                type="text"
                value={formData.course_name}
                onChange={(e) =>
                  setFormData((prev) => ({
                    ...prev,
                    course_name: e.target.value,
                  }))
                }
                className="input-field"
                required
              />
              {errors.course_name && (
                <p className="text-red-500 text-xs mt-1">
                  {errors.course_name}
                </p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Course Code *
              </label>
              <input
                type="text"
                value={formData.course_code}
                onChange={(e) =>
                  setFormData((prev) => ({
                    ...prev,
                    course_code: e.target.value,
                  }))
                }
                className="input-field"
                required
              />
              {errors.course_code && (
                <p className="text-red-500 text-xs mt-1">
                  {errors.course_code}
                </p>
              )}
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Description
            </label>
            <textarea
              value={formData.description}
              onChange={(e) =>
                setFormData((prev) => ({
                  ...prev,
                  description: e.target.value,
                }))
              }
              rows={3}
              className="input-field"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Prerequisites
            </label>
            <textarea
              value={formData.prerequisites}
              onChange={(e) =>
                setFormData((prev) => ({
                  ...prev,
                  prerequisites: e.target.value,
                }))
              }
              rows={2}
              className="input-field"
              placeholder="Enter course prerequisites..."
            />
          </div>

          <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Credits *
              </label>
              <input
                type="number"
                value={formData.credits}
                onChange={(e) =>
                  setFormData((prev) => ({ ...prev, credits: e.target.value }))
                }
                className="input-field"
                min="1"
                max="10"
                required
              />
              {errors.credits && (
                <p className="text-red-500 text-xs mt-1">{errors.credits}</p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Course Duration (months) *
              </label>
              <input
                type="number"
                value={formData.course_duration}
                onChange={(e) =>
                  setFormData((prev) => ({
                    ...prev,
                    course_duration: e.target.value,
                  }))
                }
                className="input-field"
                min="1"
                max="72"
                required
                placeholder="Enter duration in months"
              />
              {errors.course_duration && (
                <p className="text-red-500 text-xs mt-1">
                  {errors.course_duration}
                </p>
              )}
            </div>
          </div>

          <div>
            <label className="flex items-center">
              <input
                type="checkbox"
                checked={formData.is_active}
                onChange={(e) =>
                  setFormData((prev) => ({
                    ...prev,
                    is_active: e.target.checked,
                  }))
                }
                className="rounded border-gray-300 text-primary-600 shadow-sm focus:border-primary-300 focus:ring focus:ring-primary-200 focus:ring-opacity-50"
              />
              <span className="ml-2 text-sm text-gray-700">Active Course</span>
            </label>
          </div>

          <div className="flex space-x-3 pt-4">
            <button
              type="button"
              onClick={onClose}
              className="btn-secondary flex-1"
              disabled={loading}
            >
              Cancel
            </button>
            <button
              type="submit"
              className="btn-primary flex-1"
              disabled={loading}
            >
              {loading
                ? "Saving..."
                : type === "create"
                ? "Create Course"
                : "Update Course"}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CoursesPage;
