import React, { useState, useEffect } from "react";
import { useAuth } from "../../context/AuthContext";
import { authAPI, studentsAPI } from "../../services/api";
import {
  User,
  Mail,
  Phone,
  Edit,
  Save,
  X,
  Lock,
  AlertCircle,
  CheckCircle,
} from "lucide-react";

const StudentProfilePage = () => {
  const { user, login } = useAuth();
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(false);
  const [changingPassword, setChangingPassword] = useState(false);
  const [formData, setFormData] = useState({});
  const [passwordData, setPasswordData] = useState({
    current_password: "",
    new_password: "",
    confirm_password: "",
  });
  const [errors, setErrors] = useState({});
  const [success, setSuccess] = useState("");

  useEffect(() => {
    fetchProfile();
  }, []);

  const fetchProfile = async () => {
    try {
      setLoading(true);

      if (user?.role === "student") {
        // Get student profile
        const response = await studentsAPI.getProfile();
        setProfile(response.data);
        setFormData({
          first_name: response.data.user_details?.first_name || "",
          last_name: response.data.user_details?.last_name || "",
          email: response.data.user_details?.email || "",
          phone: response.data.user_details?.phone || "",
        });
      } else {
        // For admin users, create a profile from user data
        setProfile({
          user: user,
          student_number: null,
          phone_number: "",
          course: null,
          status: "admin",
        });
        setFormData({
          first_name: user?.first_name || "",
          last_name: user?.last_name || "",
          email: user?.email || "",
          phone: "",
        });
      }
    } catch (error) {
      console.error("Error fetching profile:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = () => {
    setEditing(true);
    setErrors({});
    setSuccess("");
  };

  const handleCancel = () => {
    setEditing(false);
    setFormData({
      first_name: profile?.user_details?.first_name || "",
      last_name: profile?.user_details?.last_name || "",
      email: profile?.user_details?.email || "",
      phone_number: profile?.user_details?.phone || "",
    });
    setErrors({});
  };

  const handleSave = async () => {
    try {
      setErrors({});
      setLoading(true);

      // Always use auth API to update user profile fields
      await authAPI.updateProfile(formData);

      setSuccess("Profile updated successfully!");
      setEditing(false);

      // Refresh profile data
      await fetchProfile();

      // Update auth context if user data changed
      if (
        formData.first_name !== user.first_name ||
        formData.last_name !== user.last_name
      ) {
        const updatedUser = {
          ...user,
          first_name: formData.first_name,
          last_name: formData.last_name,
        };
        login(updatedUser, localStorage.getItem("access_token"));
      }
    } catch (error) {
      if (error.response?.data) {
        setErrors(error.response.data);
      } else {
        setErrors({ general: "Failed to update profile" });
      }
    } finally {
      setLoading(false);
    }
  };

  const handlePasswordChange = async () => {
    try {
      setErrors({});

      if (passwordData.new_password !== passwordData.confirm_password) {
        setErrors({ confirm_password: "Passwords do not match" });
        return;
      }

      setLoading(true);
      await authAPI.changePassword(passwordData);

      setSuccess("Password changed successfully!");
      setChangingPassword(false);
      setPasswordData({
        current_password: "",
        new_password: "",
        confirm_password: "",
      });
    } catch (error) {
      if (error.response?.data) {
        setErrors(error.response.data);
      } else {
        setErrors({ password: "Failed to change password" });
      }
    } finally {
      setLoading(false);
    }
  };

  if (loading && !profile) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto space-y-6">
      {/* Header */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-6 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center">
              <div className="h-20 w-20 rounded-full bg-primary-100 flex items-center justify-center">
                <User className="h-10 w-10 text-primary-600" />
              </div>
              <div className="ml-6">
                <h1 className="text-2xl font-bold text-gray-900">
                  {profile?.user?.first_name} {profile?.user?.last_name}
                </h1>
                <p className="text-sm text-gray-500">
                  {user?.role === "admin" ? "Administrator" : "Student"}
                </p>
                {profile?.student_number && (
                  <p className="text-sm text-gray-500">
                    Student ID: {profile.student_number}
                  </p>
                )}
              </div>
            </div>
            <div className="flex space-x-3">
              {!editing && (
                <button
                  onClick={handleEdit}
                  className="btn-primary flex items-center"
                >
                  <Edit className="h-4 w-4 mr-2" />
                  Edit Profile
                </button>
              )}
              {editing && (
                <>
                  <button
                    onClick={handleCancel}
                    className="btn-secondary flex items-center"
                  >
                    <X className="h-4 w-4 mr-2" />
                    Cancel
                  </button>
                  <button
                    onClick={handleSave}
                    className="btn-primary flex items-center"
                    disabled={loading}
                  >
                    <Save className="h-4 w-4 mr-2" />
                    {loading ? "Saving..." : "Save"}
                  </button>
                </>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Success Message */}
      {success && (
        <div className="bg-green-50 border border-green-200 rounded-md p-4">
          <div className="flex">
            <CheckCircle className="h-5 w-5 text-green-400" />
            <div className="ml-3">
              <p className="text-sm font-medium text-green-800">{success}</p>
            </div>
            <button
              onClick={() => setSuccess("")}
              className="ml-auto text-green-400 hover:text-green-600"
            >
              <X className="h-4 w-4" />
            </button>
          </div>
        </div>
      )}

      {/* Error Messages */}
      {errors.general && (
        <div className="bg-red-50 border border-red-200 rounded-md p-4">
          <div className="flex">
            <AlertCircle className="h-5 w-5 text-red-400" />
            <div className="ml-3">
              <p className="text-sm font-medium text-red-800">
                {errors.general}
              </p>
            </div>
          </div>
        </div>
      )}

      <div className="grid grid-cols-1 gap-6 lg:grid-cols-3">
        {/* Personal Information */}
        <div className="lg:col-span-2">
          <div className="bg-white shadow rounded-lg">
            <div className="px-6 py-4 border-b border-gray-200">
              <h3 className="text-lg font-medium text-gray-900">
                Personal Information
              </h3>
            </div>
            <div className="px-6 py-4 space-y-6">
              <div className="grid grid-cols-1 gap-6 sm:grid-cols-2">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    <User className="h-4 w-4 inline mr-1" />
                    First Name
                  </label>
                  {editing ? (
                    <input
                      type="text"
                      value={formData.first_name}
                      onChange={(e) =>
                        setFormData((prev) => ({
                          ...prev,
                          first_name: e.target.value,
                        }))
                      }
                      className="input-field"
                    />
                  ) : (
                    <p className="text-sm text-gray-900">
                      {profile?.user?.first_name || "Not provided"}
                    </p>
                  )}
                  {errors.first_name && (
                    <p className="text-red-500 text-xs mt-1">
                      {errors.first_name}
                    </p>
                  )}
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    <User className="h-4 w-4 inline mr-1" />
                    Last Name
                  </label>
                  {editing ? (
                    <input
                      type="text"
                      value={formData.last_name}
                      onChange={(e) =>
                        setFormData((prev) => ({
                          ...prev,
                          last_name: e.target.value,
                        }))
                      }
                      className="input-field"
                    />
                  ) : (
                    <p className="text-sm text-gray-900">
                      {profile?.user?.last_name || "Not provided"}
                    </p>
                  )}
                  {errors.last_name && (
                    <p className="text-red-500 text-xs mt-1">
                      {errors.last_name}
                    </p>
                  )}
                </div>
              </div>

              <div className="grid grid-cols-1 gap-6 sm:grid-cols-2">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    <Mail className="h-4 w-4 inline mr-1" />
                    Email
                  </label>
                  {editing ? (
                    <input
                      type="email"
                      value={formData.email}
                      onChange={(e) =>
                        setFormData((prev) => ({
                          ...prev,
                          email: e.target.value,
                        }))
                      }
                      className="input-field"
                    />
                  ) : (
                    <p className="text-sm text-gray-900">
                      {profile?.user?.email || "Not provided"}
                    </p>
                  )}
                  {errors.email && (
                    <p className="text-red-500 text-xs mt-1">{errors.email}</p>
                  )}
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    <Phone className="h-4 w-4 inline mr-1" />
                    Phone Number
                  </label>
                  {editing ? (
                    <input
                      type="tel"
                      value={formData.phone_number}
                      onChange={(e) =>
                        setFormData((prev) => ({
                          ...prev,
                          phone_number: e.target.value,
                        }))
                      }
                      className="input-field"
                    />
                  ) : (
                    <p className="text-sm text-gray-900">
                      {profile?.phone_number || "Not provided"}
                    </p>
                  )}
                  {errors.phone_number && (
                    <p className="text-red-500 text-xs mt-1">
                      {errors.phone_number}
                    </p>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Sidebar */}
        <div className="space-y-6">
          {/* Academic Information (for students) */}
          {user?.role === "student" && (
            <div className="bg-white shadow rounded-lg">
              <div className="px-6 py-4 border-b border-gray-200">
                <h3 className="text-lg font-medium text-gray-900">
                  Academic Information
                </h3>
              </div>
              <div className="px-6 py-4 space-y-4">
                <div>
                  <p className="text-sm font-medium text-gray-500">
                    Student Number
                  </p>
                  <p className="text-sm text-gray-900">
                    {profile?.student_number || "Not assigned"}
                  </p>
                </div>

                <div>
                  <p className="text-sm font-medium text-gray-500">Course</p>
                  {profile?.course ? (
                    <div>
                      <p className="text-sm text-gray-900">
                        {profile.course.course_name}
                      </p>
                      <p className="text-xs text-gray-500">
                        {profile.course.course_code}
                      </p>
                    </div>
                  ) : (
                    <p className="text-sm text-gray-900">Not enrolled</p>
                  )}
                </div>

                <div>
                  <p className="text-sm font-medium text-gray-500">Status</p>
                  <span
                    className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                      profile?.status === "active"
                        ? "bg-green-100 text-green-800"
                        : profile?.status === "graduated"
                        ? "bg-blue-100 text-blue-800"
                        : profile?.status === "admin"
                        ? "bg-purple-100 text-purple-800"
                        : "bg-red-100 text-red-800"
                    }`}
                  >
                    {profile?.status || "Unknown"}
                  </span>
                </div>

                {profile?.enrollment_date && (
                  <div>
                    <p className="text-sm font-medium text-gray-500">
                      Enrollment Date
                    </p>
                    <p className="text-sm text-gray-900">
                      {new Date(profile.enrollment_date).toLocaleDateString()}
                    </p>
                  </div>
                )}
              </div>
            </div>
          )}

          {/* Security */}
          <div className="bg-white shadow rounded-lg">
            <div className="px-6 py-4 border-b border-gray-200">
              <h3 className="text-lg font-medium text-gray-900">Security</h3>
            </div>
            <div className="px-6 py-4">
              {!changingPassword ? (
                <button
                  onClick={() => setChangingPassword(true)}
                  className="btn-secondary w-full flex items-center justify-center"
                >
                  <Lock className="h-4 w-4 mr-2" />
                  Change Password
                </button>
              ) : (
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Current Password
                    </label>
                    <input
                      type="password"
                      value={passwordData.current_password}
                      onChange={(e) =>
                        setPasswordData((prev) => ({
                          ...prev,
                          current_password: e.target.value,
                        }))
                      }
                      className="input-field"
                    />
                    {errors.current_password && (
                      <p className="text-red-500 text-xs mt-1">
                        {errors.current_password}
                      </p>
                    )}
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      New Password
                    </label>
                    <input
                      type="password"
                      value={passwordData.new_password}
                      onChange={(e) =>
                        setPasswordData((prev) => ({
                          ...prev,
                          new_password: e.target.value,
                        }))
                      }
                      className="input-field"
                    />
                    {errors.new_password && (
                      <p className="text-red-500 text-xs mt-1">
                        {errors.new_password}
                      </p>
                    )}
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Confirm New Password
                    </label>
                    <input
                      type="password"
                      value={passwordData.confirm_password}
                      onChange={(e) =>
                        setPasswordData((prev) => ({
                          ...prev,
                          confirm_password: e.target.value,
                        }))
                      }
                      className="input-field"
                    />
                    {errors.confirm_password && (
                      <p className="text-red-500 text-xs mt-1">
                        {errors.confirm_password}
                      </p>
                    )}
                  </div>

                  <div className="flex space-x-3">
                    <button
                      onClick={() => {
                        setChangingPassword(false);
                        setPasswordData({
                          current_password: "",
                          new_password: "",
                          confirm_password: "",
                        });
                        setErrors({});
                      }}
                      className="btn-secondary flex-1"
                    >
                      Cancel
                    </button>
                    <button
                      onClick={handlePasswordChange}
                      className="btn-primary flex-1"
                      disabled={loading}
                    >
                      {loading ? "Saving..." : "Save"}
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>

          {/* Account Information */}
          <div className="bg-white shadow rounded-lg">
            <div className="px-6 py-4 border-b border-gray-200">
              <h3 className="text-lg font-medium text-gray-900">
                Account Information
              </h3>
            </div>
            <div className="px-6 py-4 space-y-3">
              <div>
                <p className="text-sm font-medium text-gray-500">Username</p>
                <p className="text-sm text-gray-900">
                  {profile?.user?.username}
                </p>
              </div>

              <div>
                <p className="text-sm font-medium text-gray-500">Role</p>
                <p className="text-sm text-gray-900 capitalize">{user?.role}</p>
              </div>

              <div>
                <p className="text-sm font-medium text-gray-500">Last Login</p>
                <p className="text-sm text-gray-900">
                  {user?.last_login
                    ? new Date(user.last_login).toLocaleString()
                    : "Never"}
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default StudentProfilePage;
