import React, { useState, useEffect } from "react";
import { useAuth } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import { coursesAPI, studentsAPI } from "../services/api";
import {
  BookOpen,
  Users,
  GraduationCap,
  TrendingUp,
  UserCheck,
  Calendar,
  AlertCircle,
} from "lucide-react";

const DashboardPage = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [stats, setStats] = useState({
    totalCourses: 0,
    activeCourses: 0,
    totalStudents: 0,
    activeStudents: 0,
  });
  const [recentCourses, setRecentCourses] = useState([]);
  const [recentStudents, setRecentStudents] = useState([]);
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showCourseModal, setShowCourseModal] = useState(false);
  const [enrolling, setEnrolling] = useState(null); // Track which course is being enrolled in
  const [enrollmentSuccess, setEnrollmentSuccess] = useState(null);
  const [studentProfile, setStudentProfile] = useState(null);
  const [enrolledCourses, setEnrolledCourses] = useState([]);
  const [availableCourses, setAvailableCourses] = useState([]);

  // Function to format course description and prerequisites
  const formatCourseText = (text) => {
    if (!text) return [];

    // Split by bullet points (â— or â€¢) and filter out empty strings
    const items = text
      .split(/[â—â€¢]/)
      .map((item) => item.trim())
      .filter((item) => item.length > 0);

    return items;
  };

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const handleAddCourse = () => {
    setShowCourseModal(true);
  };

  const handleAddStudent = () => {
    navigate("/students");
  };

  const handleViewReports = () => {
    navigate("/courses");
  };

  const handleManageCourses = () => {
    navigate("/courses");
  };

  const handleEnrollInCourse = async (courseId) => {
    try {
      setEnrolling(courseId);
      console.log("ðŸŽ¯ Starting enrollment for course:", courseId);

      // Get student profile to get student_id
      console.log("ðŸ“ Fetching student profile...");
      const profileResponse = await studentsAPI.getProfile();
      console.log("ðŸ‘¤ Profile response:", profileResponse);
      console.log("ðŸ“Š Profile data structure:", profileResponse.data);

      const studentId = profileResponse.data.student_id;
      console.log("ðŸ†” Student ID:", studentId);

      if (!studentId) {
        throw new Error("Student ID not found in profile");
      }

      // Call the enrollment API
      console.log("ðŸ“š Enrolling student in course...");
      const enrollmentResponse = await coursesAPI.enrollStudent(courseId, {
        student_id: studentId,
      });
      console.log("âœ… Enrollment response:", enrollmentResponse);

      setEnrollmentSuccess(courseId);
      // Refresh dashboard data to show updated enrollment
      fetchDashboardData();
      // Hide success message after 3 seconds
      setTimeout(() => setEnrollmentSuccess(null), 3000);
    } catch (error) {
      console.error("âŒ Enrollment error:", error);
      console.error("âŒ Error details:", error.response?.data);
      console.error("âŒ Error status:", error.response?.status);
      // Add user-friendly error message
      alert(
        `Enrollment failed: ${error.response?.data?.error || error.message}`
      );
    } finally {
      setEnrolling(null);
    }
  };

  const fetchDashboardData = async () => {
    try {
      setLoading(true);

      if (user.role === "admin") {
        // Admin dashboard - fetch summary data
        const coursesResponse = await coursesAPI.list({ limit: 5 });
        const activeCoursesResponse = await coursesAPI.listActive({ limit: 5 });
        const studentsResponse = await studentsAPI.list({ limit: 5 });
        const activeStudentsResponse = await studentsAPI.listActive({
          limit: 5,
        });

        console.log("ðŸ“Š Dashboard - Students data:", studentsResponse.data);
        console.log(
          "ðŸ“‹ Dashboard - First student structure:",
          studentsResponse.data.results?.[0]
        );

        // Fetch all courses for student modal
        const allCoursesResponse = await coursesAPI.list({ is_active: true });

        setStats({
          totalCourses:
            coursesResponse.data.count ||
            coursesResponse.data.results?.length ||
            0,
          activeCourses:
            activeCoursesResponse.data.count ||
            activeCoursesResponse.data.results?.length ||
            0,
          totalStudents:
            studentsResponse.data.count ||
            studentsResponse.data.results?.length ||
            0,
          activeStudents:
            activeStudentsResponse.data.count ||
            activeStudentsResponse.data.results?.length ||
            0,
        });

        setRecentCourses(
          coursesResponse.data.results || coursesResponse.data || []
        );
        setRecentStudents(
          studentsResponse.data.results || studentsResponse.data || []
        );
        setCourses(
          allCoursesResponse.data.results || allCoursesResponse.data || []
        );
      } else {
        // Student dashboard - fetch all available courses and student profile
        const [allCoursesResponse, profileResponse] = await Promise.all([
          coursesAPI.list({ is_active: true }),
          studentsAPI.getProfile(),
        ]);

        setRecentCourses(
          allCoursesResponse.data.results || allCoursesResponse.data || []
        );
        setStudentProfile(profileResponse);
      }
    } catch (error) {
      console.error("Error fetching dashboard data:", error);
    } finally {
      setLoading(false);
    }
  };

  const statsCards = [
    {
      name: "Total Courses",
      stat: stats.totalCourses,
      icon: BookOpen,
      color: "bg-blue-500",
    },
    {
      name: "Active Courses",
      stat: stats.activeCourses,
      icon: TrendingUp,
      color: "bg-green-500",
    },
    {
      name: "Total Students",
      stat: stats.totalStudents,
      icon: Users,
      color: "bg-purple-500",
    },
    {
      name: "Active Students",
      stat: stats.activeStudents,
      icon: UserCheck,
      color: "bg-orange-500",
    },
  ];

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Welcome Section */}
      <div className="bg-white overflow-hidden shadow rounded-lg">
        <div className="px-4 py-5 sm:p-6">
          <div className="flex items-center">
            <div className="flex-shrink-0">
              <GraduationCap className="h-12 w-12 text-primary-600" />
            </div>
            <div className="ml-5 w-0 flex-1">
              <dl>
                <dt className="text-sm font-medium text-gray-500 truncate">
                  Welcome back, {user?.first_name || user?.username}!
                </dt>
                <dd className="text-lg font-medium text-gray-900">
                  {user?.role === "admin"
                    ? "Administrator Dashboard"
                    : "Student Dashboard"}
                </dd>
              </dl>
            </div>
          </div>
        </div>
      </div>

      {/* Admin Dashboard Content */}
      {user?.role === "admin" && (
        <>
          {/* Stats Cards */}
          <div className="grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-4">
            {statsCards.map((item) => {
              const Icon = item.icon;
              return (
                <div
                  key={item.name}
                  className="bg-white overflow-hidden shadow rounded-lg"
                >
                  <div className="p-5">
                    <div className="flex items-center">
                      <div className="flex-shrink-0">
                        <div className={`${item.color} p-3 rounded-md`}>
                          <Icon className="h-6 w-6 text-white" />
                        </div>
                      </div>
                      <div className="ml-5 w-0 flex-1">
                        <dl>
                          <dt className="text-sm font-medium text-gray-500 truncate">
                            {item.name}
                          </dt>
                          <dd className="text-lg font-medium text-gray-900">
                            {item.stat}
                          </dd>
                        </dl>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>

          {/* Recent Data */}
          <div className="grid grid-cols-1 gap-6 lg:grid-cols-2">
            {/* Recent Courses */}
            <div className="bg-white shadow rounded-lg">
              <div className="px-4 py-5 sm:p-6">
                <div className="flex items-center justify-between mb-4">
                  <h3 className="text-lg leading-6 font-medium text-gray-900">
                    Recent Courses
                  </h3>
                  <div className="flex items-center space-x-2">
                    <button
                      onClick={() => navigate("/courses")}
                      className="text-sm text-primary-600 hover:text-primary-900"
                    >
                      View All
                    </button>
                    <BookOpen className="h-5 w-5 text-gray-400" />
                  </div>
                </div>
                <div className="space-y-3">
                  {recentCourses.length > 0 ? (
                    recentCourses.slice(0, 5).map((course) => (
                      <div
                        key={course.course_id}
                        className="flex justify-between items-center py-2 border-b border-gray-100 last:border-b-0"
                      >
                        <div>
                          <p className="text-sm font-medium text-gray-900">
                            {course.course_name}
                          </p>
                          <p className="text-xs text-gray-500">
                            {course.course_code} â€¢ {course.credits} credits
                          </p>
                        </div>
                        <div className="text-right">
                          <span
                            className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                              course.is_active
                                ? "bg-green-100 text-green-800"
                                : "bg-red-100 text-red-800"
                            }`}
                          >
                            {course.is_active ? "Active" : "Inactive"}
                          </span>
                          <p className="text-xs text-gray-500 mt-1">
                            {course.enrolled_students_count || 0} students
                          </p>
                        </div>
                      </div>
                    ))
                  ) : (
                    <p className="text-gray-500 text-center py-4">
                      No courses found
                    </p>
                  )}
                </div>
              </div>
            </div>

            {/* Recent Students */}
            <div className="bg-white shadow rounded-lg">
              <div className="px-4 py-5 sm:p-6">
                <div className="flex items-center justify-between mb-4">
                  <h3 className="text-lg leading-6 font-medium text-gray-900">
                    Recent Students
                  </h3>
                  <div className="flex items-center space-x-2">
                    <button
                      onClick={() => navigate("/students")}
                      className="text-sm text-primary-600 hover:text-primary-900"
                    >
                      View All
                    </button>
                    <Users className="h-5 w-5 text-gray-400" />
                  </div>
                </div>
                <div className="space-y-3">
                  {recentStudents.length > 0 ? (
                    recentStudents.slice(0, 5).map((student) => (
                      <div
                        key={student.student_id}
                        className="flex items-center justify-between py-3 border-b border-gray-100 last:border-b-0"
                      >
                        <div className="flex-1">
                          <div className="flex items-center space-x-3">
                            <div className="flex-shrink-0 h-8 w-8 bg-blue-100 rounded-full flex items-center justify-center">
                              <Users className="h-4 w-4 text-blue-600" />
                            </div>
                            <div className="flex-1 min-w-0">
                              <p className="text-sm font-medium text-gray-900 truncate">
                                {student.user_details?.first_name ||
                                  student.user?.first_name}{" "}
                                {student.user_details?.last_name ||
                                  student.user?.last_name}
                                {student.full_name &&
                                  !student.user_details &&
                                  !student.user && (
                                    <span>{student.full_name}</span>
                                  )}
                              </p>
                              <p className="text-xs text-gray-500 truncate">
                                {student.user_details?.email ||
                                  student.user?.email ||
                                  student.email}
                              </p>
                              <div className="flex items-center space-x-2 mt-1">
                                <span className="text-xs text-gray-600">
                                  ID: {student.student_number}
                                </span>
                                <span className="text-xs text-gray-400">â€¢</span>
                                <span className="text-xs text-gray-600">
                                  Course:{" "}
                                  {student.course_details?.course_name ||
                                    student.course?.course_name ||
                                    student.course?.course_code ||
                                    "Not enrolled"}
                                </span>
                              </div>
                            </div>
                          </div>
                        </div>
                        <div className="flex-shrink-0 ml-3">
                          <span
                            className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                              student.status === "active"
                                ? "bg-green-100 text-green-800"
                                : student.status === "graduated"
                                ? "bg-blue-100 text-blue-800"
                                : "bg-red-100 text-red-800"
                            }`}
                          >
                            {student.status}
                          </span>
                        </div>
                      </div>
                    ))
                  ) : (
                    <p className="text-gray-500 text-center py-4">
                      No students found
                    </p>
                  )}
                </div>
              </div>
            </div>
          </div>

          {/* Quick Actions */}
          {user?.role === "admin" && (
            <div className="bg-white shadow rounded-lg">
              <div className="px-4 py-5 sm:p-6">
                <h3 className="text-lg leading-6 font-medium text-gray-900 mb-4">
                  Quick Actions
                </h3>
                <div className="grid grid-cols-1 gap-4 sm:grid-cols-3">
                  <button
                    onClick={handleAddCourse}
                    className="btn-primary flex items-center justify-center"
                  >
                    <BookOpen className="h-4 w-4 mr-2" />
                    Add New Course
                  </button>
                  <button
                    onClick={handleManageCourses}
                    className="btn-secondary flex items-center justify-center"
                  >
                    <BookOpen className="h-4 w-4 mr-2" />
                    Manage Courses
                  </button>
                  <button
                    onClick={handleAddStudent}
                    className="btn-primary flex items-center justify-center"
                  >
                    <Users className="h-4 w-4 mr-2" />
                    Add New Student
                  </button>
                  <button
                    onClick={() => navigate("/students")}
                    className="btn-secondary flex items-center justify-center"
                  >
                    <Users className="h-4 w-4 mr-2" />
                    Manage Students
                  </button>
                </div>
              </div>
            </div>
          )}
        </>
      )}

      {/* Student Dashboard Content */}
      {user?.role === "student" && (
        <>
          {/* Student Info Card */}
          <div className="bg-white shadow rounded-lg">
            <div className="px-4 py-5 sm:p-6">
              <h3 className="text-lg leading-6 font-medium text-gray-900 mb-4">
                My Information
              </h3>
              <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
                <div>
                  <p className="text-sm font-medium text-gray-500">
                    Student ID
                  </p>
                  <p className="text-lg text-gray-900">
                    {user?.student_number || "Not assigned"}
                  </p>
                </div>
                <div>
                  <p className="text-sm font-medium text-gray-500">Email</p>
                  <p className="text-lg text-gray-900">{user?.email}</p>
                </div>
                <div>
                  <p className="text-sm font-medium text-gray-500">Status</p>
                  <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">
                    Active
                  </span>
                </div>
                <div>
                  <p className="text-sm font-medium text-gray-500">
                    Enrollment
                  </p>
                  <p className="text-lg text-gray-900">Current Student</p>
                </div>
              </div>
            </div>
          </div>

          {/* Available Courses */}
          <div className="bg-white shadow rounded-lg">
            <div className="px-4 py-5 sm:p-6">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg leading-6 font-medium text-gray-900">
                  Available Courses
                </h3>
                <BookOpen className="h-5 w-5 text-gray-400" />
              </div>
              {enrollmentSuccess && (
                <div className="mb-4 bg-green-50 border border-green-200 rounded-lg p-4">
                  <div className="flex items-center">
                    <div className="flex-shrink-0">
                      <svg
                        className="h-5 w-5 text-green-400"
                        viewBox="0 0 20 20"
                        fill="currentColor"
                      >
                        <path
                          fillRule="evenodd"
                          d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z"
                          clipRule="evenodd"
                        />
                      </svg>
                    </div>
                    <div className="ml-3">
                      <p className="text-sm font-medium text-green-800">
                        Successfully enrolled in course!
                      </p>
                    </div>
                  </div>
                </div>
              )}
              <div className="space-y-4">
                {recentCourses.length > 0 ? (
                  recentCourses.map((course) => (
                    <div
                      key={course.course_id}
                      className="border border-gray-200 rounded-lg p-6 hover:shadow-md transition-shadow"
                    >
                      <div className="flex justify-between items-start">
                        <div className="flex-1">
                          <h4 className="text-xl font-semibold text-gray-900 mb-2">
                            {course.course_name}
                          </h4>
                          <div className="flex items-center space-x-4 mb-3">
                            <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-sm font-medium bg-blue-100 text-blue-800">
                              {course.course_code}
                            </span>
                            <span className="text-sm text-gray-500">
                              {course.credits} credits
                            </span>
                            {course.course_duration && (
                              <span className="text-sm text-gray-500">
                                {course.course_duration}{" "}
                                {course.course_duration === 1
                                  ? "semester"
                                  : "semesters"}
                              </span>
                            )}
                          </div>
                          <div className="mb-4">
                            <h5 className="text-sm font-medium text-gray-700 mb-2">
                              Course Objectives:
                            </h5>
                            {formatCourseText(course.description).length > 0 ? (
                              <ul className="text-sm text-gray-600 space-y-1">
                                {formatCourseText(course.description).map(
                                  (objective, index) => (
                                    <li
                                      key={index}
                                      className="flex items-start"
                                    >
                                      <span className="inline-block w-2 h-2 bg-blue-400 rounded-full mt-2 mr-3 flex-shrink-0"></span>
                                      <span>{objective}</span>
                                    </li>
                                  )
                                )}
                              </ul>
                            ) : (
                              <p className="text-sm text-gray-600">
                                No description available
                              </p>
                            )}
                          </div>
                          {course.prerequisites && (
                            <div className="mb-4">
                              <h5 className="text-sm font-medium text-gray-700 mb-2">
                                Prerequisites:
                              </h5>
                              <ul className="text-sm text-gray-600 space-y-1">
                                {formatCourseText(course.prerequisites).map(
                                  (prerequisite, index) => (
                                    <li
                                      key={index}
                                      className="flex items-start"
                                    >
                                      <span className="inline-block w-2 h-2 bg-red-400 rounded-full mt-2 mr-3 flex-shrink-0"></span>
                                      <span>{prerequisite}</span>
                                    </li>
                                  )
                                )}
                              </ul>
                            </div>
                          )}
                          <div className="flex items-center space-x-4">
                            <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">
                              {course.is_active ? "Active" : "Inactive"}
                            </span>
                            <span className="text-sm text-gray-500">
                              {course.enrolled_students_count || 0} students
                              enrolled
                            </span>
                          </div>
                        </div>
                        <div className="ml-6 flex-shrink-0">
                          <button
                            onClick={() =>
                              handleEnrollInCourse(course.course_id)
                            }
                            disabled={
                              enrolling === course.course_id ||
                              !course.is_active
                            }
                            className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                              course.is_active
                                ? "bg-primary-600 text-white hover:bg-primary-700 disabled:bg-primary-400"
                                : "bg-gray-300 text-gray-500 cursor-not-allowed"
                            }`}
                          >
                            {enrolling === course.course_id ? (
                              <div className="flex items-center">
                                <svg
                                  className="animate-spin -ml-1 mr-2 h-4 w-4 text-white"
                                  xmlns="http://www.w3.org/2000/svg"
                                  fill="none"
                                  viewBox="0 0 24 24"
                                >
                                  <circle
                                    className="opacity-25"
                                    cx="12"
                                    cy="12"
                                    r="10"
                                    stroke="currentColor"
                                    strokeWidth="4"
                                  ></circle>
                                  <path
                                    className="opacity-75"
                                    fill="currentColor"
                                    d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                                  ></path>
                                </svg>
                                Enrolling...
                              </div>
                            ) : enrollmentSuccess === course.course_id ? (
                              "Enrolled âœ“"
                            ) : course.is_active ? (
                              "Enroll Now"
                            ) : (
                              "Not Available"
                            )}
                          </button>
                        </div>
                      </div>
                    </div>
                  ))
                ) : (
                  <div className="text-center py-8">
                    <BookOpen className="mx-auto h-12 w-12 text-gray-400" />
                    <h3 className="mt-2 text-sm font-medium text-gray-900">
                      No courses available
                    </h3>
                    <p className="mt-1 text-sm text-gray-500">
                      Check back later for new course offerings.
                    </p>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Student Quick Actions */}
          <div className="bg-white shadow rounded-lg">
            <div className="px-4 py-5 sm:p-6">
              <h3 className="text-lg leading-6 font-medium text-gray-900 mb-4">
                Quick Actions
              </h3>
              <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
                <button
                  onClick={() => navigate("/courses")}
                  className="btn-primary flex items-center justify-center"
                >
                  <BookOpen className="h-4 w-4 mr-2" />
                  View Available Courses
                </button>
                <button
                  onClick={() => navigate("/profile")}
                  className="btn-secondary flex items-center justify-center"
                >
                  <Users className="h-4 w-4 mr-2" />
                  Update Profile
                </button>
              </div>
            </div>
          </div>
        </>
      )}

      {/* Course Creation Modal */}
      {showCourseModal && (
        <CourseModal
          type="create"
          course={null}
          onClose={() => setShowCourseModal(false)}
          onSuccess={() => {
            setShowCourseModal(false);
            fetchDashboardData(); // Refresh dashboard data
          }}
        />
      )}
    </div>
  );
};

// Course Modal Component
const CourseModal = ({ type, course, onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    course_name: course?.course_name || "",
    course_code: course?.course_code || "",
    description: course?.description || "",
    prerequisites: course?.prerequisites || "",
    course_duration: course?.course_duration || "",
    credits: course?.credits || "",
    is_active: course?.is_active ?? true,
  });
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setErrors({});

    try {
      if (type === "create") {
        await coursesAPI.create(formData);
      } else if (type === "edit") {
        await coursesAPI.update(course.course_id, formData);
      } else if (type === "delete") {
        await coursesAPI.delete(course.course_id);
      }
      onSuccess();
    } catch (error) {
      if (error.response?.data) {
        setErrors(error.response.data);
      }
    } finally {
      setLoading(false);
    }
  };

  if (type === "delete") {
    return (
      <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center z-50">
        <div className="bg-white rounded-lg p-6 max-w-md w-full mx-4">
          <div className="flex items-center mb-4">
            <AlertCircle className="h-6 w-6 text-red-600 mr-3" />
            <h3 className="text-lg font-medium text-gray-900">Delete Course</h3>
          </div>
          <p className="text-sm text-gray-500 mb-4">
            Are you sure you want to delete "{course?.course_name}"? This action
            cannot be undone.
          </p>
          <div className="flex space-x-3">
            <button
              onClick={onClose}
              className="btn-secondary flex-1"
              disabled={loading}
            >
              Cancel
            </button>
            <button
              onClick={handleSubmit}
              className="btn-danger flex-1"
              disabled={loading}
            >
              {loading ? "Deleting..." : "Delete"}
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 max-w-2xl w-full mx-4 max-h-screen overflow-y-auto">
        <h3 className="text-lg font-medium text-gray-900 mb-6">
          {type === "create" ? "Create New Course" : "Edit Course"}
        </h3>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Course Name *
              </label>
              <input
                type="text"
                value={formData.course_name}
                onChange={(e) =>
                  setFormData((prev) => ({
                    ...prev,
                    course_name: e.target.value,
                  }))
                }
                className="input-field"
                required
              />
              {errors.course_name && (
                <p className="text-red-500 text-xs mt-1">
                  {errors.course_name}
                </p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Course Code *
              </label>
              <input
                type="text"
                value={formData.course_code}
                onChange={(e) =>
                  setFormData((prev) => ({
                    ...prev,
                    course_code: e.target.value,
                  }))
                }
                className="input-field"
                required
              />
              {errors.course_code && (
                <p className="text-red-500 text-xs mt-1">
                  {errors.course_code}
                </p>
              )}
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Description
            </label>
            <textarea
              value={formData.description}
              onChange={(e) =>
                setFormData((prev) => ({
                  ...prev,
                  description: e.target.value,
                }))
              }
              rows={3}
              className="input-field"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Prerequisites
            </label>
            <textarea
              value={formData.prerequisites}
              onChange={(e) =>
                setFormData((prev) => ({
                  ...prev,
                  prerequisites: e.target.value,
                }))
              }
              rows={2}
              className="input-field"
              placeholder="Enter course prerequisites..."
            />
          </div>

          <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Credits *
              </label>
              <input
                type="number"
                value={formData.credits}
                onChange={(e) =>
                  setFormData((prev) => ({ ...prev, credits: e.target.value }))
                }
                className="input-field"
                min="1"
                max="10"
                required
              />
              {errors.credits && (
                <p className="text-red-500 text-xs mt-1">{errors.credits}</p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Course Duration (months) *
              </label>
              <input
                type="number"
                value={formData.course_duration}
                onChange={(e) =>
                  setFormData((prev) => ({
                    ...prev,
                    course_duration: e.target.value,
                  }))
                }
                className="input-field"
                min="1"
                max="72"
                required
                placeholder="Enter duration in months"
              />
              {errors.course_duration && (
                <p className="text-red-500 text-xs mt-1">
                  {errors.course_duration}
                </p>
              )}
            </div>
          </div>

          <div>
            <label className="flex items-center">
              <input
                type="checkbox"
                checked={formData.is_active}
                onChange={(e) =>
                  setFormData((prev) => ({
                    ...prev,
                    is_active: e.target.checked,
                  }))
                }
                className="rounded border-gray-300 text-primary-600 shadow-sm focus:border-primary-300 focus:ring focus:ring-primary-200 focus:ring-opacity-50"
              />
              <span className="ml-2 text-sm text-gray-700">Active Course</span>
            </label>
          </div>

          <div className="flex space-x-3 pt-4">
            <button
              type="button"
              onClick={onClose}
              className="btn-secondary flex-1"
              disabled={loading}
            >
              Cancel
            </button>
            <button
              type="submit"
              className="btn-primary flex-1"
              disabled={loading}
            >
              {loading
                ? "Saving..."
                : type === "create"
                ? "Create Course"
                : "Update Course"}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default DashboardPage;
