import { useState, useEffect, useCallback, useMemo } from 'react';
import { coursesAPI } from '../services/api';

/**
 * Custom hook for managing course search and filtering logic
 */
export const useCourseSearch = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [filters, setFilters] = useState({
    is_active: "",
  });
  const [pagination, setPagination] = useState({
    currentPage: 1,
    totalPages: 1,
    totalCount: 0,
  });

  // Debounced search function
  const [debouncedSearchTerm, setDebouncedSearchTerm] = useState(searchTerm);

  useEffect(() => {
    const timer = setTimeout(() => {
      setDebouncedSearchTerm(searchTerm);
    }, 300);

    return () => clearTimeout(timer);
  }, [searchTerm]);

  // Memoized search parameters
  const searchParams = useMemo(() => ({
    page: pagination.currentPage,
    search: debouncedSearchTerm,
    ...filters,
  }), [pagination.currentPage, debouncedSearchTerm, filters]);

  // Fetch courses function
  const fetchCourses = useCallback(async (params = searchParams) => {
    try {
      setLoading(true);
      setError(null);
      
      console.log("ðŸ” Fetching courses with params:", params);
      const response = await coursesAPI.list(params);
      console.log("ðŸ“Š API Response:", response.data);
      
      setCourses(response.data.results || response.data || []);

      if (response.data.count !== undefined) {
        setPagination((prev) => ({
          ...prev,
          totalCount: response.data.count,
          totalPages: Math.ceil(response.data.count / 10),
        }));
      }
    } catch (err) {
      console.error("âŒ Error fetching courses:", err);
      setError(err.response?.data?.detail || 'Failed to fetch courses');
      setCourses([]);
    } finally {
      setLoading(false);
    }
  }, [searchParams]);

  // Effect to fetch courses when search params change
  useEffect(() => {
    fetchCourses();
  }, [fetchCourses]);

  // Search handlers
  const handleSearchChange = useCallback((value) => {
    console.log("ðŸ”Ž Search term changed to:", value);
    setSearchTerm(value);
    setPagination(prev => ({ ...prev, currentPage: 1 })); // Reset to first page
  }, []);

  const handleFilterChange = useCallback((filterKey, value) => {
    console.log(`ðŸŽ›ï¸ Filter ${filterKey} changed to:`, value);
    setFilters(prev => ({ ...prev, [filterKey]: value }));
    setPagination(prev => ({ ...prev, currentPage: 1 })); // Reset to first page
  }, []);

  const handlePageChange = useCallback((page) => {
    setPagination(prev => ({ ...prev, currentPage: page }));
  }, []);

  // Clear search and filters
  const clearSearch = useCallback(() => {
    setSearchTerm("");
    setFilters({ is_active: "" });
    setPagination(prev => ({ ...prev, currentPage: 1 }));
  }, []);

  // Refresh data
  const refreshCourses = useCallback(() => {
    fetchCourses();
  }, [fetchCourses]);

  return {
    // Data
    courses,
    loading,
    error,
    
    // Search state
    searchTerm,
    filters,
    pagination,
    
    // Actions
    handleSearchChange,
    handleFilterChange,
    handlePageChange,
    clearSearch,
    refreshCourses,
    
    // Computed values
    hasResults: courses.length > 0,
    isEmpty: !loading && courses.length === 0,
    hasFilters: searchTerm || filters.is_active,
  };
};
