import React from "react";
import { BookOpen, Search, Plus } from "lucide-react";

/**
 * Reusable EmptyState component
 * Displays appropriate empty state messages with actions
 */
const EmptyState = ({
  type = "no-data",
  title,
  description,
  actionLabel,
  onAction,
  hasFilters = false,
  onClearFilters,
  className = "",
}) => {
  const getEmptyStateConfig = () => {
    switch (type) {
      case "no-results":
        return {
          icon: Search,
          defaultTitle: "No courses found",
          defaultDescription: hasFilters
            ? "No courses match your current search criteria. Try adjusting your filters or search terms."
            : "No courses match your search.",
          showClearAction: hasFilters,
        };
      case "no-data":
        return {
          icon: BookOpen,
          defaultTitle: "No courses yet",
          defaultDescription: "Get started by creating your first course.",
          showClearAction: false,
        };
      default:
        return {
          icon: BookOpen,
          defaultTitle: "No content",
          defaultDescription: "There's nothing here yet.",
          showClearAction: false,
        };
    }
  };

  const config = getEmptyStateConfig();
  const Icon = config.icon;

  return (
    <div className={`text-center py-12 px-4 ${className}`}>
      <div className="max-w-md mx-auto">
        {/* Icon */}
        <div className="mx-auto h-16 w-16 flex items-center justify-center rounded-full bg-gray-100 mb-4">
          <Icon className="h-8 w-8 text-gray-400" />
        </div>

        {/* Title */}
        <h3 className="text-lg font-medium text-gray-900 mb-2">
          {title || config.defaultTitle}
        </h3>

        {/* Description */}
        <p className="text-gray-500 mb-6">
          {description || config.defaultDescription}
        </p>

        {/* Actions */}
        <div className="flex flex-col sm:flex-row gap-3 justify-center">
          {/* Clear Filters Action */}
          {config.showClearAction && onClearFilters && (
            <button
              onClick={onClearFilters}
              className="inline-flex items-center justify-center px-4 py-2 border border-gray-300 rounded-md shadow-sm bg-white text-sm font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            >
              <Search className="h-4 w-4 mr-2" />
              Clear filters
            </button>
          )}

          {/* Primary Action */}
          {actionLabel && onAction && (
            <button
              onClick={onAction}
              className="inline-flex items-center justify-center px-4 py-2 border border-transparent rounded-md shadow-sm bg-blue-600 text-sm font-medium text-white hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            >
              <Plus className="h-4 w-4 mr-2" />
              {actionLabel}
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default EmptyState;
