import React, { useState, useEffect } from "react";
import { useAuth } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import { coursesAPI, studentsAPI } from "../services/api";
import {
  BookOpen,
  Users,
  GraduationCap,
  TrendingUp,
  UserCheck,
  Calendar,
  AlertCircle,
  Eye,
  X,
} from "lucide-react";

const DashboardPage = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [stats, setStats] = useState({
    totalCourses: 0,
    activeCourses: 0,
    totalStudents: 0,
    activeStudents: 0,
  });
  const [recentCourses, setRecentCourses] = useState([]);
  const [recentStudents, setRecentStudents] = useState([]);
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showCourseModal, setShowCourseModal] = useState(false);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const handleAddCourse = () => {
    setShowCourseModal(true);
  };

  const handleAddStudent = () => {
    navigate("/students");
  };

  const handleViewReports = () => {
    navigate("/courses");
  };

  const fetchDashboardData = async () => {
    try {
      setLoading(true);

      // Fetch courses data
      const coursesResponse = await coursesAPI.list({ limit: 5 });
      const activeCoursesResponse = await coursesAPI.listActive({ limit: 5 });

      // Fetch students data
      const studentsResponse = await studentsAPI.list({ limit: 5 });
      const activeStudentsResponse = await studentsAPI.listActive({ limit: 5 });

      // Fetch all courses for student modal
      const allCoursesResponse = await coursesAPI.list({ is_active: true });

      setStats({
        totalCourses:
          coursesResponse.data.count ||
          coursesResponse.data.results?.length ||
          0,
        activeCourses:
          activeCoursesResponse.data.count ||
          activeCoursesResponse.data.results?.length ||
          0,
        totalStudents:
          studentsResponse.data.count ||
          studentsResponse.data.results?.length ||
          0,
        activeStudents:
          activeStudentsResponse.data.count ||
          activeStudentsResponse.data.results?.length ||
          0,
      });

      setRecentCourses(
        coursesResponse.data.results || coursesResponse.data || []
      );
      setRecentStudents(
        studentsResponse.data.results || studentsResponse.data || []
      );
      setCourses(
        allCoursesResponse.data.results || allCoursesResponse.data || []
      );
    } catch (error) {
      console.error("Error fetching dashboard data:", error);
    } finally {
      setLoading(false);
    }
  };

  const statsCards = [
    {
      name: "Total Courses",
      stat: stats.totalCourses,
      icon: BookOpen,
      color: "bg-blue-500",
    },
    {
      name: "Active Courses",
      stat: stats.activeCourses,
      icon: TrendingUp,
      color: "bg-green-500",
    },
    {
      name: "Total Students",
      stat: stats.totalStudents,
      icon: Users,
      color: "bg-purple-500",
    },
    {
      name: "Active Students",
      stat: stats.activeStudents,
      icon: UserCheck,
      color: "bg-orange-500",
    },
  ];

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Welcome Section */}
      <div className="bg-white overflow-hidden shadow rounded-lg">
        <div className="px-4 py-5 sm:p-6">
          <div className="flex items-center">
            <div className="flex-shrink-0">
              <GraduationCap className="h-12 w-12 text-primary-600" />
            </div>
            <div className="ml-5 w-0 flex-1">
              <dl>
                <dt className="text-sm font-medium text-gray-500 truncate">
                  Welcome back, {user?.first_name || user?.username}!
                </dt>
                <dd className="text-lg font-medium text-gray-900">
                  {user?.role === "admin"
                    ? "Administrator Dashboard"
                    : "Student Dashboard"}
                </dd>
              </dl>
            </div>
          </div>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-4">
        {statsCards.map((item) => {
          const Icon = item.icon;
          return (
            <div
              key={item.name}
              className="bg-white overflow-hidden shadow rounded-lg"
            >
              <div className="p-5">
                <div className="flex items-center">
                  <div className="flex-shrink-0">
                    <div className={`${item.color} p-3 rounded-md`}>
                      <Icon className="h-6 w-6 text-white" />
                    </div>
                  </div>
                  <div className="ml-5 w-0 flex-1">
                    <dl>
                      <dt className="text-sm font-medium text-gray-500 truncate">
                        {item.name}
                      </dt>
                      <dd className="text-lg font-medium text-gray-900">
                        {item.stat}
                      </dd>
                    </dl>
                  </div>
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {/* Recent Data */}
      <div className="grid grid-cols-1 gap-6 lg:grid-cols-2">
        {/* Recent Courses */}
        <div className="bg-white shadow rounded-lg">
          <div className="px-4 py-5 sm:p-6">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg leading-6 font-medium text-gray-900">
                Recent Courses
              </h3>
              <div className="flex items-center space-x-2">
                <button
                  onClick={() => navigate("/courses")}
                  className="text-sm text-primary-600 hover:text-primary-900"
                >
                  View All
                </button>
                <BookOpen className="h-5 w-5 text-gray-400" />
              </div>
            </div>
            <div className="space-y-3">
              {recentCourses.length > 0 ? (
                recentCourses.slice(0, 5).map((course) => (
                  <div
                    key={course.course_id}
                    className="flex justify-between items-center py-2 border-b border-gray-100 last:border-b-0"
                  >
                    <div>
                      <p className="text-sm font-medium text-gray-900">
                        {course.course_name}
                      </p>
                      <p className="text-xs text-gray-500">
                        {course.course_code}  {course.credits} credits
                      </p>
                    </div>
                    <div className="text-right">
                      <span
                        className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                          course.is_active
                            ? "bg-green-100 text-green-800"
                            : "bg-red-100 text-red-800"
                        }`}
                      >
                        {course.is_active ? "Active" : "Inactive"}
                      </span>
                      <p className="text-xs text-gray-500 mt-1">
                        {course.enrolled_students_count || 0} students
                      </p>
                    </div>
                  </div>
                ))
              ) : (
                <p className="text-gray-500 text-center py-4">
                  No courses found
                </p>
              )}
            </div>
          </div>
        </div>

        {/* Recent Students */}
        <div className="bg-white shadow rounded-lg">
          <div className="px-4 py-5 sm:p-6">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg leading-6 font-medium text-gray-900">
                Recent Students
              </h3>
              <div className="flex items-center space-x-2">
                <button
                  onClick={() => navigate("/students")}
                  className="text-sm text-primary-600 hover:text-primary-900"
                >
                  View All
                </button>
                <Users className="h-5 w-5 text-gray-400" />
              </div>
            </div>
            <div className="space-y-3">
              {recentStudents.length > 0 ? (
                recentStudents.slice(0, 5).map((student) => (
                  <div
                    key={student.student_id}
                    className="flex justify-between items-center py-2 border-b border-gray-100 last:border-b-0"
                  >
                    <div>
                      <p className="text-sm font-medium text-gray-900">
                        {student.user?.first_name} {student.user?.last_name}
                      </p>
                      <p className="text-xs text-gray-500">
                        {student.student_number}
                      </p>
                    </div>
                    <div className="text-right">
                      <span
                        className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                          student.status === "active"
                            ? "bg-green-100 text-green-800"
                            : student.status === "graduated"
                            ? "bg-blue-100 text-blue-800"
                            : "bg-red-100 text-red-800"
                        }`}
                      >
                        {student.status}
                      </span>
                      <p className="text-xs text-gray-500 mt-1">
                        {student.course?.course_code || "No course"}
                      </p>
                    </div>
                  </div>
                ))
              ) : (
                <p className="text-gray-500 text-center py-4">
                  No students found
                </p>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Quick Actions */}
      {user?.role === "admin" && (
        <div className="bg-white shadow rounded-lg">
          <div className="px-4 py-5 sm:p-6">
            <h3 className="text-lg leading-6 font-medium text-gray-900 mb-4">
              Quick Actions
            </h3>
            <div className="grid grid-cols-1 gap-4 sm:grid-cols-3">
              <button
                onClick={handleAddCourse}
                className="btn-primary flex items-center justify-center"
              >
                <BookOpen className="h-4 w-4 mr-2" />
                Add New Course
              </button>
              <button
                onClick={handleAddStudent}
                className="btn-primary flex items-center justify-center"
              >
                <Users className="h-4 w-4 mr-2" />
                Add New Student
              </button>
              <button
                onClick={handleViewReports}
                className="btn-secondary flex items-center justify-center"
              >
                <Calendar className="h-4 w-4 mr-2" />
                View Reports
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Course Creation Modal */}
      {showCourseModal && (
        <CourseModal
          type="create"
          course={null}
          onClose={() => setShowCourseModal(false)}
          onSuccess={() => {
            setShowCourseModal(false);
            fetchDashboardData(); // Refresh dashboard data
          }}
        />
      )}
    </div>
  );
};

// Course Modal Component
const CourseModal = ({ type, course, onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    course_name: course?.course_name || "",
    course_code: course?.course_code || "",
    description: course?.description || "",
    prerequisites: course?.prerequisites || "",
    course_duration: course?.course_duration || "",
    credits: course?.credits || "",
    is_active: course?.is_active ?? true,
  });
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setErrors({});

    try {
      if (type === "create") {
        await coursesAPI.create(formData);
      } else if (type === "edit") {
        await coursesAPI.update(course.course_id, formData);
      } else if (type === "delete") {
        await coursesAPI.delete(course.course_id);
      }
      onSuccess();
    } catch (error) {
      if (error.response?.data) {
        setErrors(error.response.data);
      }
    } finally {
      setLoading(false);
    }
  };

  if (type === "delete") {
    return (
      <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center z-50">
        <div className="bg-white rounded-lg p-6 max-w-md w-full mx-4">
          <div className="flex items-center mb-4">
            <AlertCircle className="h-6 w-6 text-red-600 mr-3" />
            <h3 className="text-lg font-medium text-gray-900">Delete Course</h3>
          </div>
          <p className="text-sm text-gray-500 mb-4">
            Are you sure you want to delete "{course?.course_name}"? This action
            cannot be undone.
          </p>
          <div className="flex space-x-3">
            <button
              onClick={onClose}
              className="btn-secondary flex-1"
              disabled={loading}
            >
              Cancel
            </button>
            <button
              onClick={handleSubmit}
              className="btn-danger flex-1"
              disabled={loading}
            >
              {loading ? "Deleting..." : "Delete"}
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 max-w-2xl w-full mx-4 max-h-screen overflow-y-auto">
        <h3 className="text-lg font-medium text-gray-900 mb-6">
          {type === "create" ? "Create New Course" : "Edit Course"}
        </h3>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Course Name *
              </label>
              <input
                type="text"
                value={formData.course_name}
                onChange={(e) =>
                  setFormData((prev) => ({
                    ...prev,
                    course_name: e.target.value,
                  }))
                }
                className="input-field"
                required
              />
              {errors.course_name && (
                <p className="text-red-500 text-xs mt-1">
                  {errors.course_name}
                </p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Course Code *
              </label>
              <input
                type="text"
                value={formData.course_code}
                onChange={(e) =>
                  setFormData((prev) => ({
                    ...prev,
                    course_code: e.target.value,
                  }))
                }
                className="input-field"
                required
              />
              {errors.course_code && (
                <p className="text-red-500 text-xs mt-1">
                  {errors.course_code}
                </p>
              )}
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Description
            </label>
            <textarea
              value={formData.description}
              onChange={(e) =>
                setFormData((prev) => ({
                  ...prev,
                  description: e.target.value,
                }))
              }
              rows={3}
              className="input-field"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Prerequisites
            </label>
            <textarea
              value={formData.prerequisites}
              onChange={(e) =>
                setFormData((prev) => ({
                  ...prev,
                  prerequisites: e.target.value,
                }))
              }
              rows={2}
              className="input-field"
              placeholder="Enter course prerequisites..."
            />
          </div>

          <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Credits *
              </label>
              <input
                type="number"
                value={formData.credits}
                onChange={(e) =>
                  setFormData((prev) => ({ ...prev, credits: e.target.value }))
                }
                className="input-field"
                min="1"
                max="10"
                required
              />
              {errors.credits && (
                <p className="text-red-500 text-xs mt-1">{errors.credits}</p>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Course Duration (months) *
              </label>
              <input
                type="number"
                value={formData.course_duration}
                onChange={(e) =>
                  setFormData((prev) => ({
                    ...prev,
                    course_duration: e.target.value,
                  }))
                }
                className="input-field"
                min="1"
                max="72"
                required
                placeholder="Enter duration in months"
              />
              {errors.course_duration && (
                <p className="text-red-500 text-xs mt-1">
                  {errors.course_duration}
                </p>
              )}
            </div>
          </div>

          <div>
            <label className="flex items-center">
              <input
                type="checkbox"
                checked={formData.is_active}
                onChange={(e) =>
                  setFormData((prev) => ({
                    ...prev,
                    is_active: e.target.checked,
                  }))
                }
                className="rounded border-gray-300 text-primary-600 shadow-sm focus:border-primary-300 focus:ring focus:ring-primary-200 focus:ring-opacity-50"
              />
              <span className="ml-2 text-sm text-gray-700">Active Course</span>
            </label>
          </div>

          <div className="flex space-x-3 pt-4">
            <button
              type="button"
              onClick={onClose}
              className="btn-secondary flex-1"
              disabled={loading}
            >
              Cancel
            </button>
            <button
              type="submit"
              className="btn-primary flex-1"
              disabled={loading}
            >
              {loading
                ? "Saving..."
                : type === "create"
                ? "Create Course"
                : "Update Course"}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

// All Courses Modal Component
const AllCoursesModal = ({ courses, onClose, onRefresh }) => {
  const [searchTerm, setSearchTerm] = React.useState("");
  const [statusFilter, setStatusFilter] = React.useState("all");

  const filteredCourses = courses.filter((course) => {
    const matchesSearch =
      course.course_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      course.course_code?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      course.description?.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesStatus =
      statusFilter === "all" ||
      (statusFilter === "active" && course.is_active) ||
      (statusFilter === "inactive" && !course.is_active);

    return matchesSearch && matchesStatus;
  });

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg shadow-xl max-w-6xl w-full max-h-[90vh] overflow-hidden">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <div>
            <h3 className="text-lg font-medium text-gray-900">All Courses</h3>
            <p className="text-sm text-gray-500 mt-1">
              {filteredCourses.length} course
              {filteredCourses.length !== 1 ? "s" : ""} found
            </p>
          </div>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600 transition-colors"
          >
            <X className="h-6 w-6" />
          </button>
        </div>

        {/* Filters */}
        <div className="p-6 border-b border-gray-200 bg-gray-50">
          <div className="flex flex-col sm:flex-row gap-4">
            <div className="flex-1">
              <input
                type="text"
                placeholder="Search courses by name, code, or description..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500"
              />
            </div>
            <div>
              <select
                value={statusFilter}
                onChange={(e) => setStatusFilter(e.target.value)}
                className="px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500"
              >
                <option value="all">All Status</option>
                <option value="active">Active</option>
                <option value="inactive">Inactive</option>
              </select>
            </div>
            <button
              onClick={onRefresh}
              className="px-4 py-2 bg-primary-600 text-white rounded-lg hover:bg-primary-700 transition-colors"
            >
              Refresh
            </button>
          </div>
        </div>

        {/* Courses List */}
        <div className="overflow-y-auto max-h-[60vh]">
          {filteredCourses.length > 0 ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 p-6">
              {filteredCourses.map((course) => (
                <div
                  key={course.course_id}
                  className="bg-white border border-gray-200 rounded-lg p-4 hover:shadow-md transition-shadow"
                >
                  {/* Course Header */}
                  <div className="flex items-start justify-between mb-3">
                    <div className="flex-1">
                      <h4 className="text-lg font-semibold text-gray-900 mb-1">
                        {course.course_name}
                      </h4>
                      <p className="text-sm text-gray-600 font-mono">
                        {course.course_code}
                      </p>
                    </div>
                    <span
                      className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                        course.is_active
                          ? "bg-green-100 text-green-800"
                          : "bg-red-100 text-red-800"
                      }`}
                    >
                      {course.is_active ? "Active" : "Inactive"}
                    </span>
                  </div>

                  {/* Course Details */}
                  <div className="space-y-2 text-sm">
                    {course.description && (
                      <div>
                        <span className="font-medium text-gray-700">
                          Description:
                        </span>
                        <p
                          className="text-gray-600 mt-1 text-xs overflow-hidden"
                          style={{
                            display: "-webkit-box",
                            WebkitLineClamp: 3,
                            WebkitBoxOrient: "vertical",
                            maxHeight: "3.6em",
                          }}
                        >
                          {course.description}
                        </p>
                      </div>
                    )}

                    <div className="grid grid-cols-2 gap-2">
                      <div>
                        <span className="font-medium text-gray-700">
                          Credits:
                        </span>
                        <p className="text-gray-600">
                          {course.credits || "N/A"}
                        </p>
                      </div>
                      <div>
                        <span className="font-medium text-gray-700">
                          Duration:
                        </span>
                        <p className="text-gray-600">
                          {course.course_duration
                            ? `${course.course_duration} months`
                            : "N/A"}
                        </p>
                      </div>
                    </div>

                    {course.prerequisites && (
                      <div>
                        <span className="font-medium text-gray-700">
                          Prerequisites:
                        </span>
                        <p
                          className="text-gray-600 text-xs mt-1 overflow-hidden"
                          style={{
                            display: "-webkit-box",
                            WebkitLineClamp: 2,
                            WebkitBoxOrient: "vertical",
                            maxHeight: "2.4em",
                          }}
                        >
                          {course.prerequisites}
                        </p>
                      </div>
                    )}

                    <div>
                      <span className="font-medium text-gray-700">
                        Enrolled Students:
                      </span>
                      <p className="text-gray-600">
                        {course.enrolled_students_count || 0}
                      </p>
                    </div>

                    {course.created_at && (
                      <div>
                        <span className="font-medium text-gray-700">
                          Created:
                        </span>
                        <p className="text-gray-600 text-xs">
                          {new Date(course.created_at).toLocaleDateString()}
                        </p>
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="flex flex-col items-center justify-center py-12">
              <BookOpen className="h-12 w-12 text-gray-400 mb-4" />
              <h3 className="text-lg font-medium text-gray-900 mb-2">
                No courses found
              </h3>
              <p className="text-gray-500 text-center max-w-md">
                {searchTerm || statusFilter !== "all"
                  ? "Try adjusting your search criteria or filters."
                  : "No courses are available at the moment."}
              </p>
            </div>
          )}
        </div>

        {/* Footer */}
        <div className="flex items-center justify-between p-6 border-t border-gray-200 bg-gray-50">
          <p className="text-sm text-gray-600">
            Showing {filteredCourses.length} of {courses.length} course
            {courses.length !== 1 ? "s" : ""}
          </p>
          <button
            onClick={onClose}
            className="px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition-colors"
          >
            Close
          </button>
        </div>
      </div>
    </div>
  );
};

export default DashboardPage;
