import React, { useState, useEffect } from "react";
import { useAuth } from "../context/AuthContext";
import { studentsAPI, coursesAPI } from "../services/api";
import {
  BookOpen,
  GraduationCap,
  CheckCircle,
  Calendar,
  Clock,
  Users,
  Award,
  TrendingUp,
  FileText,
  ExternalLink,
  Star,
} from "lucide-react";

const MyEnrolledCoursesPage = () => {
  const { user } = useAuth();
  const [enrolledCourses, setEnrolledCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [studentProfile, setStudentProfile] = useState(null);
  const [courseDetails, setCourseDetails] = useState({});

  useEffect(() => {
    if (user?.role === "student") {
      fetchStudentProfile();
    }
  }, [user]);

  const fetchStudentProfile = async () => {
    try {
      setLoading(true);
      console.log("🎓 Fetching student profile for enrolled courses...");

      const profileResponse = await studentsAPI.getProfile();
      const studentData = profileResponse.data || profileResponse;
      setStudentProfile(studentData);

      // Get enrolled courses - assuming the student can have multiple courses
      if (studentData?.course) {
        // If single course enrollment
        const singleCourse = Array.isArray(studentData.course)
          ? studentData.course
          : [studentData.course];
        setEnrolledCourses(singleCourse);

        // Fetch detailed course information for each enrolled course
        await fetchCourseDetails(singleCourse);
      } else {
        setEnrolledCourses([]);
      }
    } catch (error) {
      console.error("❌ Error fetching student profile:", error);
      setEnrolledCourses([]);
    } finally {
      setLoading(false);
    }
  };

  const fetchCourseDetails = async (courses) => {
    try {
      const courseDetailsMap = {};

      for (const course of courses) {
        const courseId = course.course_id || course.id;
        if (courseId) {
          try {
            const response = await coursesAPI.get(courseId);
            courseDetailsMap[courseId] = response.data;
          } catch (error) {
            console.error(
              `Error fetching details for course ${courseId}:`,
              error
            );
            courseDetailsMap[courseId] = course; // Fallback to basic course data
          }
        }
      }

      setCourseDetails(courseDetailsMap);
    } catch (error) {
      console.error("Error fetching course details:", error);
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return "Not set";
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "long",
      day: "numeric",
    });
  };

  const formatCourseText = (text) => {
    if (!text) return [];

    const items = text
      .split(/[●•]/)
      .map((item) => item.trim())
      .filter((item) => item.length > 0);

    return items;
  };

  const getEnrollmentDate = () => {
    return (
      studentProfile?.created_at ||
      studentProfile?.enrollment_date ||
      new Date().toISOString()
    );
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header Section */}
      <div className="bg-gradient-to-r from-blue-600 to-indigo-700 rounded-lg shadow-lg p-6 text-white">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold flex items-center">
              <GraduationCap className="h-8 w-8 mr-3" />
              My Enrolled Courses
            </h1>
            <p className="text-blue-100 mt-2">
              Track your academic progress and course details
            </p>
          </div>
          <div className="text-right">
            <p className="text-sm text-blue-200">Welcome back,</p>
            <p className="text-xl font-semibold">
              {user?.first_name || "Student"}
            </p>
          </div>
        </div>
      </div>

      {/* Student Stats Overview */}
      {studentProfile && (
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
          <div className="bg-white rounded-lg shadow-md p-6">
            <div className="flex items-center">
              <div className="p-3 rounded-full bg-blue-100">
                <BookOpen className="h-6 w-6 text-blue-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-500">
                  Enrolled Courses
                </p>
                <p className="text-2xl font-bold text-gray-900">
                  {enrolledCourses.length}
                </p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow-md p-6">
            <div className="flex items-center">
              <div className="p-3 rounded-full bg-green-100">
                <CheckCircle className="h-6 w-6 text-green-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-500">Student ID</p>
                <p className="text-2xl font-bold text-gray-900">
                  {studentProfile.student_id}
                </p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow-md p-6">
            <div className="flex items-center">
              <div className="p-3 rounded-full bg-purple-100">
                <Calendar className="h-6 w-6 text-purple-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-500">
                  Enrollment Date
                </p>
                <p className="text-lg font-bold text-gray-900">
                  {formatDate(getEnrollmentDate())}
                </p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow-md p-6">
            <div className="flex items-center">
              <div className="p-3 rounded-full bg-yellow-100">
                <Award className="h-6 w-6 text-yellow-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-500">
                  Academic Status
                </p>
                <p className="text-lg font-bold text-green-600">Active</p>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Enrolled Courses Section */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-2xl font-bold text-gray-900 flex items-center">
            <BookOpen className="h-6 w-6 mr-2 text-blue-600" />
            Course Details
          </h2>
          {enrolledCourses.length > 0 && (
            <span className="bg-blue-100 text-blue-800 text-sm font-medium px-3 py-1 rounded-full">
              {enrolledCourses.length} Course
              {enrolledCourses.length !== 1 ? "s" : ""} Enrolled
            </span>
          )}
        </div>

        {enrolledCourses.length === 0 ? (
          <div className="text-center py-12">
            <BookOpen className="mx-auto h-16 w-16 text-gray-400 mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">
              No Enrolled Courses
            </h3>
            <p className="text-gray-500 mb-6">
              You haven't enrolled in any courses yet. Explore available courses
              to get started.
            </p>
            <button
              onClick={() => (window.location.href = "/courses")}
              className="inline-flex items-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
            >
              <ExternalLink className="h-4 w-4 mr-2" />
              Browse Courses
            </button>
          </div>
        ) : (
          <div className="space-y-6">
            {enrolledCourses.map((course, index) => {
              const courseId = course.course_id || course.id;
              const detailedCourse = courseDetails[courseId] || course;

              return (
                <div
                  key={courseId || index}
                  className="border border-green-200 rounded-lg p-6 bg-green-50 hover:bg-green-100 transition-colors"
                >
                  {/* Course Header */}
                  <div className="flex items-start justify-between mb-4">
                    <div className="flex-1">
                      <div className="flex items-center mb-2">
                        <CheckCircle className="h-5 w-5 text-green-600 mr-2" />
                        <h3 className="text-xl font-bold text-gray-900">
                          {detailedCourse.course_name}
                        </h3>
                        <span className="ml-3 inline-flex items-center px-2 py-1 rounded-full text-xs font-semibold bg-green-100 text-green-800 border border-green-300">
                          Enrolled
                        </span>
                      </div>
                      <p className="text-sm text-gray-600 mb-2">
                        <span className="font-semibold">Course Code:</span>{" "}
                        <span className="font-mono text-blue-600">
                          {detailedCourse.course_code}
                        </span>
                      </p>
                      {detailedCourse.is_active !== undefined && (
                        <span
                          className={`inline-flex items-center px-3 py-1 rounded-full text-xs font-semibold ${
                            detailedCourse.is_active
                              ? "bg-green-100 text-green-800 border border-green-200"
                              : "bg-red-100 text-red-800 border border-red-200"
                          }`}
                        >
                          ● {detailedCourse.is_active ? "Active" : "Inactive"}
                        </span>
                      )}
                    </div>
                  </div>

                  {/* Course Details Grid */}
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
                    <div className="bg-white p-4 rounded-lg border">
                      <div className="flex items-center space-x-2 mb-2">
                        <Star className="h-4 w-4 text-yellow-500" />
                        <span className="text-sm font-medium text-gray-700">
                          Credits
                        </span>
                      </div>
                      <p className="text-xl font-bold text-gray-900">
                        {detailedCourse.credits || "3"}
                      </p>
                    </div>

                    <div className="bg-white p-4 rounded-lg border">
                      <div className="flex items-center space-x-2 mb-2">
                        <Clock className="h-4 w-4 text-blue-500" />
                        <span className="text-sm font-medium text-gray-700">
                          Duration
                        </span>
                      </div>
                      <p className="text-xl font-bold text-gray-900">
                        {detailedCourse.course_duration
                          ? `${detailedCourse.course_duration} months`
                          : "1 semester"}
                      </p>
                    </div>

                    <div className="bg-white p-4 rounded-lg border">
                      <div className="flex items-center space-x-2 mb-2">
                        <Users className="h-4 w-4 text-purple-500" />
                        <span className="text-sm font-medium text-gray-700">
                          Students
                        </span>
                      </div>
                      <p className="text-xl font-bold text-gray-900">
                        {detailedCourse.enrolled_students_count || "0"}
                      </p>
                    </div>
                  </div>

                  {/* Course Description */}
                  {detailedCourse.description && (
                    <div className="mb-4">
                      <h4 className="text-sm font-semibold text-gray-700 mb-3 flex items-center">
                        <FileText className="h-4 w-4 mr-2" />
                        Course Objectives
                      </h4>
                      {formatCourseText(detailedCourse.description).length >
                      0 ? (
                        <ul className="text-sm text-gray-600 space-y-2 bg-white p-4 rounded-lg border">
                          {formatCourseText(detailedCourse.description).map(
                            (objective, idx) => (
                              <li key={idx} className="flex items-start">
                                <span className="inline-block w-2 h-2 bg-blue-400 rounded-full mt-2 mr-3 flex-shrink-0"></span>
                                <span>{objective}</span>
                              </li>
                            )
                          )}
                        </ul>
                      ) : (
                        <div className="bg-white p-4 rounded-lg border">
                          <p className="text-sm text-gray-600">
                            {detailedCourse.description}
                          </p>
                        </div>
                      )}
                    </div>
                  )}

                  {/* Prerequisites */}
                  {detailedCourse.prerequisites && (
                    <div className="mb-4">
                      <h4 className="text-sm font-semibold text-gray-700 mb-3 flex items-center">
                        <TrendingUp className="h-4 w-4 mr-2" />
                        Prerequisites
                      </h4>
                      {formatCourseText(detailedCourse.prerequisites).length >
                      0 ? (
                        <ul className="text-sm text-gray-600 space-y-2 bg-white p-4 rounded-lg border">
                          {formatCourseText(detailedCourse.prerequisites).map(
                            (prerequisite, idx) => (
                              <li key={idx} className="flex items-start">
                                <span className="inline-block w-2 h-2 bg-red-400 rounded-full mt-2 mr-3 flex-shrink-0"></span>
                                <span>{prerequisite}</span>
                              </li>
                            )
                          )}
                        </ul>
                      ) : (
                        <div className="bg-white p-4 rounded-lg border">
                          <p className="text-sm text-gray-600">
                            {detailedCourse.prerequisites}
                          </p>
                        </div>
                      )}
                    </div>
                  )}

                  {/* Course Timeline */}
                  <div className="bg-white p-4 rounded-lg border">
                    <h4 className="text-sm font-semibold text-gray-700 mb-3 flex items-center">
                      <Calendar className="h-4 w-4 mr-2" />
                      Course Timeline
                    </h4>
                    <div className="grid grid-cols-2 gap-4 text-sm">
                      <div>
                        <span className="text-gray-500 block">Start Date</span>
                        <span className="font-medium text-gray-900">
                          {formatDate(detailedCourse.start_date)}
                        </span>
                      </div>
                      <div>
                        <span className="text-gray-500 block">End Date</span>
                        <span className="font-medium text-gray-900">
                          {formatDate(detailedCourse.end_date)}
                        </span>
                      </div>
                    </div>
                  </div>

                  {/* Instructor Information */}
                  {detailedCourse.instructor && (
                    <div className="mt-4 pt-4 border-t border-gray-200">
                      <div className="flex items-center space-x-2 text-sm">
                        <Users className="h-4 w-4 text-gray-500" />
                        <span className="text-gray-500">Instructor:</span>
                        <span className="font-medium text-gray-900">
                          {detailedCourse.instructor.first_name}{" "}
                          {detailedCourse.instructor.last_name}
                        </span>
                      </div>
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        )}
      </div>

      {/* Quick Actions */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">
          Quick Actions
        </h3>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <button
            onClick={() => (window.location.href = "/courses")}
            className="flex items-center justify-center p-4 border-2 border-dashed border-gray-300 rounded-lg hover:border-blue-500 hover:bg-blue-50 transition-colors"
          >
            <div className="text-center">
              <ExternalLink className="h-8 w-8 text-gray-400 mx-auto mb-2" />
              <span className="text-sm font-medium text-gray-600">
                Browse More Courses
              </span>
            </div>
          </button>

          <button
            onClick={() => (window.location.href = "/dashboard")}
            className="flex items-center justify-center p-4 border-2 border-dashed border-gray-300 rounded-lg hover:border-green-500 hover:bg-green-50 transition-colors"
          >
            <div className="text-center">
              <TrendingUp className="h-8 w-8 text-gray-400 mx-auto mb-2" />
              <span className="text-sm font-medium text-gray-600">
                View Dashboard
              </span>
            </div>
          </button>

          <button
            onClick={() => (window.location.href = "/profile")}
            className="flex items-center justify-center p-4 border-2 border-dashed border-gray-300 rounded-lg hover:border-purple-500 hover:bg-purple-50 transition-colors"
          >
            <div className="text-center">
              <FileText className="h-8 w-8 text-gray-400 mx-auto mb-2" />
              <span className="text-sm font-medium text-gray-600">
                Edit Profile
              </span>
            </div>
          </button>
        </div>
      </div>
    </div>
  );
};

export default MyEnrolledCoursesPage;
