import React, { useState, useEffect } from "react";
import { useAuth } from "../context/AuthContext";
import { coursesAPI, studentsAPI } from "../services/api";
import {
  Plus,
  Search,
  Edit,
  Trash2,
  Users,
  BookOpen,
  Filter,
  X,
  GraduationCap,
  CheckCircle,
} from "lucide-react";

const CoursesPage = () => {
  const { user } = useAuth();
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [modalType, setModalType] = useState("create"); // 'create', 'edit', 'delete'
  const [filters, setFilters] = useState({
    is_active: "",
  });
  const [pagination, setPagination] = useState({
    currentPage: 1,
    totalPages: 1,
    totalCount: 0,
  });

  // Student enrollment states
  const [enrolling, setEnrolling] = useState(null);
  const [enrollmentSuccess, setEnrollmentSuccess] = useState(null);
  const [studentProfile, setStudentProfile] = useState(null);
  const [enrolledCourses, setEnrolledCourses] = useState([]);

  useEffect(() => {
    console.log("ðŸš€ CoursesPage mounted - Current user:", user);
    fetchCourses();
    if (user?.role === "student") {
      fetchStudentProfile();
    }
  }, [searchTerm, filters, pagination.currentPage]);

  const fetchStudentProfile = async () => {
    if (user?.role === "student") {
      try {
        const profileResponse = await studentsAPI.getProfile();
        const studentData = profileResponse.data || profileResponse;
        setStudentProfile(studentData);

        // Get enrolled courses
        const enrolled = studentData?.course ? [studentData.course] : [];
        setEnrolledCourses(enrolled);
      } catch (error) {
        console.error("Error fetching student profile:", error);
      }
    }
  };

  const handleEnrollInCourse = async (courseId) => {
    try {
      setEnrolling(courseId);
      console.log("ðŸŽ¯ Starting enrollment for course:", courseId);

      // Get student profile to get student_id
      const profileResponse = await studentsAPI.getProfile();
      const studentId = profileResponse.data.student_id;

      if (!studentId) {
        throw new Error("Student ID not found in profile");
      }

      // Call the enrollment API
      console.log("ðŸ“š Enrolling student in course...");
      await coursesAPI.enrollStudent(courseId, { student_id: studentId });

      setEnrollmentSuccess(courseId);

      // Refresh courses and student profile
      fetchCourses();
      fetchStudentProfile();

      // Hide success message after 3 seconds
      setTimeout(() => setEnrollmentSuccess(null), 3000);
    } catch (error) {
      console.error("âŒ Enrollment error:", error);
      alert(
        `Enrollment failed: ${error.response?.data?.error || error.message}`
      );
    } finally {
      setEnrolling(null);
    }
  };

  const fetchCourses = async () => {
    try {
      setLoading(true);
      const params = {
        page: pagination.currentPage,
        search: searchTerm,
      };

      // Only add filters that have actual values
      Object.keys(filters).forEach(key => {
        if (filters[key] && filters[key] !== "") {
          params[key] = filters[key];
        }
      });

      console.log("ðŸ” Fetching courses with params:", params);
      const response = await coursesAPI.list(params);
      console.log("ðŸ“Š Courses API Response:", response.data);
      console.log(
        "ðŸ“‹ First course data structure:",
        response.data.results?.[0]
      );
      console.log(
        "ðŸ”¢ Number of courses received:",
        response.data.results?.length || 0
      );
      console.log("ðŸ‘¤ Current user role:", user?.role);
      console.log("ðŸ·ï¸ Is staff:", user?.is_staff);
      console.log("ðŸ“¦ Raw response data:", response.data);

      setCourses(response.data.results || response.data || []);

      if (response.data.count !== undefined) {
        setPagination((prev) => ({
          ...prev,
          totalCount: response.data.count,
          totalPages: Math.ceil(response.data.count / 10),
        }));
      }
    } catch (error) {
      console.error("âŒ Error fetching courses:", error);
      setCourses([]);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateCourse = () => {
    setModalType("create");
    setSelectedCourse(null);
    setShowModal(true);
  };

  const handleEditCourse = (course) => {
    setModalType("edit");
    setSelectedCourse(course);
    setShowModal(true);
  };

  const handleDeleteCourse = (course) => {
    setModalType("delete");
    setSelectedCourse(course);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setSelectedCourse(null);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString();
  };

  // Function to format course description and prerequisites
  const formatCourseText = (text) => {
    if (!text) return [];

    const items = text
      .split(/[â—â€¢]/)
      .map((item) => item.trim())
      .filter((item) => item.length > 0);

    return items;
  };

  // Check if student is enrolled in a course
  const isEnrolledInCourse = (courseId) => {
    return enrolledCourses.some((course) => course.course_id === courseId);
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header with stats */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <div className="flex justify-between items-start mb-6">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">
              {user?.role === "student" ? "Available Courses" : "Courses"}
            </h1>
            <p className="text-gray-600 mt-2">
              {user?.role === "student"
                ? `Explore ${pagination.totalCount} courses available for enrollment`
                : `Total courses: ${pagination.totalCount}`}
            </p>
            {user?.role === "student" && enrolledCourses.length > 0 && (
              <p className="text-green-600 mt-1 flex items-center">
                <CheckCircle className="h-4 w-4 mr-1" />
                Currently enrolled in: {enrolledCourses[0].course_name}
              </p>
            )}
          </div>
          {user?.is_staff && (
            <button
              onClick={handleCreateCourse}
              className="inline-flex items-center px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            >
              <Plus className="h-4 w-4 mr-2" />
              Add Course
            </button>
          )}
          {user?.role === "student" && (
            <div className="flex items-center space-x-4">
              <div className="text-right">
                <p className="text-sm text-gray-500">Welcome back,</p>
                <p className="font-semibold text-gray-900">
                  {user?.first_name || "Student"}
                </p>
              </div>
              <GraduationCap className="h-10 w-10 text-blue-600" />
            </div>
          )}
        </div>

        {/* Enrollment Success Message */}
        {user?.role === "student" && enrollmentSuccess && (
          <div className="mb-6 bg-green-50 border border-green-200 rounded-lg p-4">
            <div className="flex items-center">
              <CheckCircle className="h-5 w-5 text-green-400 mr-2" />
              <p className="text-sm font-medium text-green-800">
                âœ… Successfully enrolled in course! Your enrollment will be
                updated shortly.
              </p>
            </div>
          </div>
        )}

        {/* Search and Filter */}
        <div className="flex flex-col md:flex-row gap-4 mb-6">
          <div className="relative flex-1">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
            <input
              type="text"
              placeholder="Search courses by name, code, or description..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            />
          </div>

          <div className="flex items-center space-x-2">
            <Filter className="h-4 w-4 text-gray-400" />
            <select
              value={filters.is_active}
              onChange={(e) =>
                setFilters((prev) => ({ ...prev, is_active: e.target.value }))
              }
              className="px-3 py-2 border border-gray-300 rounded-md text-sm focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              <option value="">All Status</option>
              <option value="true">Active</option>
              <option value="false">Inactive</option>
            </select>
          </div>

          {(searchTerm || filters.is_active) && (
            <button
              onClick={() => {
                setSearchTerm("");
                setFilters({ is_active: "" });
              }}
              className="flex items-center space-x-1 px-3 py-2 text-sm text-gray-600 hover:text-gray-800 border border-gray-300 rounded-md hover:bg-gray-50"
            >
              <X className="h-4 w-4" />
              <span>Clear</span>
            </button>
          )}
        </div>
      </div>

      {/* Courses Grid */}
      {courses.length === 0 ? (
        <div className="bg-white rounded-lg shadow-md p-12 text-center">
          <BookOpen className="mx-auto h-16 w-16 text-gray-400 mb-4" />
          <h3 className="text-lg font-medium text-gray-900 mb-2">
            No courses found
          </h3>
          <p className="text-gray-500 mb-6">
            {searchTerm || filters.is_active
              ? "Try adjusting your search or filter criteria"
              : "Get started by creating your first course"}
          </p>
          {user?.is_staff && (
            <button
              onClick={handleCreateCourse}
              className="inline-flex items-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
            >
              <Plus className="h-4 w-4 mr-2" />
              Create Course
            </button>
          )}
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {courses.map((course) => {
            const isEnrolled = isEnrolledInCourse(
              course.course_id || course.id
            );

            return (
              <div
                key={course.course_id || course.id}
                className={`bg-white rounded-lg shadow-md hover:shadow-lg transition-shadow duration-200 border ${
                  isEnrolled
                    ? "border-green-300 bg-green-50"
                    : "border-gray-200"
                }`}
              >
                {/* Course Header */}
                <div className="p-6 border-b border-gray-100">
                  <div className="flex justify-between items-start">
                    <div className="flex-1">
                      <div className="flex items-center justify-between mb-3">
                        <div className="flex items-center space-x-3">
                          <BookOpen className="h-5 w-5 text-blue-600" />
                          <h3 className="text-xl font-bold text-gray-900 leading-tight">
                            {course.course_name}
                          </h3>
                        </div>
                        {isEnrolled && (
                          <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-semibold bg-green-100 text-green-800 border border-green-300">
                            <CheckCircle className="h-3 w-3 mr-1" />
                            Enrolled
                          </span>
                        )}
                      </div>

                      <div className="mb-3">
                        <p className="text-sm text-gray-600">
                          <span className="font-semibold text-gray-700">
                            Code:
                          </span>{" "}
                          <span className="font-mono text-blue-600">
                            {course.course_code}
                          </span>
                        </p>
                      </div>

                      <div className="flex items-center space-x-4 mb-3">
                        <span
                          className={`inline-flex items-center px-3 py-1 rounded-full text-xs font-semibold ${
                            course.is_active
                              ? "bg-green-100 text-green-800 border border-green-200"
                              : "bg-red-100 text-red-800 border border-red-200"
                          }`}
                        >
                          â— {course.is_active ? "Active" : "Inactive"}
                        </span>
                        <span className="text-xs text-gray-500">
                          ðŸ‘¥ {course.enrolled_students_count || 0} enrolled
                        </span>
                      </div>
                    </div>

                    {user?.role === "admin" && (
                      <div className="flex space-x-2">
                        <button
                          onClick={() => handleEditCourse(course)}
                          className="p-2 text-blue-600 hover:bg-blue-50 rounded-lg transition-colors"
                          title="Edit Course"
                        >
                          <Edit className="h-4 w-4" />
                        </button>
                        <button
                          onClick={() => handleDeleteCourse(course)}
                          className="p-2 text-red-600 hover:bg-red-50 rounded-lg transition-colors"
                          title="Delete Course"
                        >
                          <Trash2 className="h-4 w-4" />
                        </button>
                      </div>
                    )}
                  </div>
                </div>

                {/* Course Content */}
                <div className="p-6">
                  {/* Course Details */}
                  <div className="grid grid-cols-2 gap-4 mb-4">
                    <div className="bg-blue-50 p-3 rounded-lg">
                      <div className="flex items-center space-x-2">
                        <BookOpen className="h-4 w-4 text-blue-600" />
                        <span className="text-xs font-medium text-blue-700 uppercase tracking-wide">
                          Credits
                        </span>
                      </div>
                      <p className="text-lg font-bold text-blue-900 mt-1">
                        {course.credits || 0}
                      </p>
                    </div>
                    <div className="bg-purple-50 p-3 rounded-lg">
                      <div className="flex items-center space-x-2">
                        <Users className="h-4 w-4 text-purple-600" />
                        <span className="text-xs font-medium text-purple-700 uppercase tracking-wide">
                          Duration
                        </span>
                      </div>
                      <p className="text-lg font-bold text-purple-900 mt-1">
                        {course.course_duration
                          ? `${course.course_duration} sem`
                          : "1 sem"}
                      </p>
                    </div>
                  </div>

                  {/* Course Description */}
                  {course.description && (
                    <div className="mb-4">
                      <h4 className="text-sm font-semibold text-gray-700 mb-2">
                        ðŸ“– Course Objectives:
                      </h4>
                      {formatCourseText(course.description).length > 0 ? (
                        <ul className="text-sm text-gray-600 space-y-1">
                          {formatCourseText(course.description)
                            .slice(0, 3)
                            .map((objective, index) => (
                              <li key={index} className="flex items-start">
                                <span className="inline-block w-2 h-2 bg-blue-400 rounded-full mt-2 mr-2 flex-shrink-0"></span>
                                <span className="line-clamp-2">
                                  {objective}
                                </span>
                              </li>
                            ))}
                          {formatCourseText(course.description).length > 3 && (
                            <li className="text-blue-600 text-xs font-medium">
                              +{formatCourseText(course.description).length - 3}{" "}
                              more objectives...
                            </li>
                          )}
                        </ul>
                      ) : (
                        <p className="text-sm text-gray-600">
                          {course.description}
                        </p>
                      )}
                    </div>
                  )}

                  {/* Prerequisites */}
                  {course.prerequisites && (
                    <div className="mb-4">
                      <h4 className="text-sm font-semibold text-gray-700 mb-2">
                        âš ï¸ Prerequisites:
                      </h4>
                      {formatCourseText(course.prerequisites).length > 0 ? (
                        <ul className="text-sm text-gray-600 space-y-1">
                          {formatCourseText(course.prerequisites)
                            .slice(0, 2)
                            .map((prerequisite, index) => (
                              <li key={index} className="flex items-start">
                                <span className="inline-block w-2 h-2 bg-red-400 rounded-full mt-2 mr-2 flex-shrink-0"></span>
                                <span className="line-clamp-1">
                                  {prerequisite}
                                </span>
                              </li>
                            ))}
                          {formatCourseText(course.prerequisites).length >
                            2 && (
                            <li className="text-red-600 text-xs font-medium">
                              +
                              {formatCourseText(course.prerequisites).length -
                                2}{" "}
                              more requirements...
                            </li>
                          )}
                        </ul>
                      ) : (
                        <p className="text-sm text-gray-600">
                          {course.prerequisites}
                        </p>
                      )}
                    </div>
                  )}

                  {/* Student Enrollment Button */}
                  {user?.role === "student" && (
                    <div className="mt-4 pt-4 border-t border-gray-200">
                      {isEnrolled ? (
                        <div className="flex items-center justify-center py-3 bg-green-100 rounded-lg">
                          <CheckCircle className="h-5 w-5 text-green-600 mr-2" />
                          <span className="font-semibold text-green-800">
                            You are enrolled in this course
                          </span>
                        </div>
                      ) : (
                        <button
                          onClick={() =>
                            handleEnrollInCourse(course.course_id || course.id)
                          }
                          disabled={
                            enrolling === (course.course_id || course.id) ||
                            !course.is_active
                          }
                          className={`w-full py-3 px-4 rounded-lg font-semibold transition-all duration-200 ${
                            course.is_active
                              ? "bg-blue-600 text-white hover:bg-blue-700 hover:shadow-lg disabled:bg-blue-400 disabled:cursor-not-allowed"
                              : "bg-gray-300 text-gray-500 cursor-not-allowed"
                          }`}
                        >
                          {enrolling === (course.course_id || course.id) ? (
                            <div className="flex items-center justify-center">
                              <svg
                                className="animate-spin -ml-1 mr-2 h-4 w-4 text-white"
                                xmlns="http://www.w3.org/2000/svg"
                                fill="none"
                                viewBox="0 0 24 24"
                              >
                                <circle
                                  className="opacity-25"
                                  cx="12"
                                  cy="12"
                                  r="10"
                                  stroke="currentColor"
                                  strokeWidth="4"
                                ></circle>
                                <path
                                  className="opacity-75"
                                  fill="currentColor"
                                  d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                                ></path>
                              </svg>
                              Enrolling...
                            </div>
                          ) : enrollmentSuccess ===
                            (course.course_id || course.id) ? (
                            <span className="flex items-center justify-center">
                              <CheckCircle className="h-4 w-4 mr-2" />
                              Enrolled Successfully!
                            </span>
                          ) : course.is_active ? (
                            <span className="flex items-center justify-center">
                              ðŸ“ Enroll in this Course
                            </span>
                          ) : (
                            "Course Not Available"
                          )}
                        </button>
                      )}
                    </div>
                  )}

                  {/* Admin Course Management */}
                  {user?.role === "admin" && course.instructor && (
                    <div className="mt-4 pt-4 border-t border-gray-200">
                      <div className="flex items-center space-x-2 text-sm">
                        <span className="text-gray-500">Instructor:</span>
                        <span className="font-medium text-gray-900">
                          {course.instructor.first_name}{" "}
                          {course.instructor.last_name}
                        </span>
                      </div>
                    </div>
                  )}
                </div>
              </div>
            );
          })}
        </div>
      )}

      {/* Pagination */}
      {pagination.totalPages > 1 && (
        <div className="bg-white rounded-lg shadow-md p-4">
          <div className="flex items-center justify-between">
            <div className="text-sm text-gray-700">
              Showing{" "}
              <span className="font-medium">
                {(pagination.currentPage - 1) * 10 + 1}
              </span>{" "}
              to{" "}
              <span className="font-medium">
                {Math.min(pagination.currentPage * 10, pagination.totalCount)}
              </span>{" "}
              of <span className="font-medium">{pagination.totalCount}</span>{" "}
              results
            </div>
            <div className="flex space-x-2">
              <button
                onClick={() =>
                  setPagination((prev) => ({
                    ...prev,
                    currentPage: prev.currentPage - 1,
                  }))
                }
                disabled={pagination.currentPage === 1}
                className="px-3 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Previous
              </button>
              <span className="px-3 py-2 text-sm font-medium text-gray-700">
                Page {pagination.currentPage} of {pagination.totalPages}
              </span>
              <button
                onClick={() =>
                  setPagination((prev) => ({
                    ...prev,
                    currentPage: prev.currentPage + 1,
                  }))
                }
                disabled={pagination.currentPage === pagination.totalPages}
                className="px-3 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Next
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Modal Placeholder */}
      {showModal && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
            <div className="mt-3">
              <h3 className="text-lg font-medium text-gray-900 mb-4">
                {modalType === "create" && "Create New Course"}
                {modalType === "edit" && "Edit Course"}
                {modalType === "delete" && "Delete Course"}
              </h3>

              {modalType === "delete" ? (
                <div>
                  <p className="text-sm text-gray-500 mb-4">
                    Are you sure you want to delete this course? This action
                    cannot be undone.
                  </p>
                  <div className="flex justify-end space-x-3">
                    <button
                      onClick={closeModal}
                      className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-200 rounded-md hover:bg-gray-300"
                    >
                      Cancel
                    </button>
                    <button className="px-4 py-2 text-sm font-medium text-white bg-red-600 rounded-md hover:bg-red-700">
                      Delete
                    </button>
                  </div>
                </div>
              ) : (
                <div>
                  <p className="text-sm text-gray-500 mb-4">
                    Course form would go here...
                  </p>
                  <div className="flex justify-end space-x-3">
                    <button
                      onClick={closeModal}
                      className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-200 rounded-md hover:bg-gray-300"
                    >
                      Cancel
                    </button>
                    <button className="px-4 py-2 text-sm font-medium text-white bg-blue-600 rounded-md hover:bg-blue-700">
                      {modalType === "create" ? "Create" : "Save"}
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CoursesPage;
