import { useState, useEffect, useCallback, useMemo } from "react";
import { coursesAPI } from "../services/api";
import { useAuth } from "../context/AuthContext";

/**
 * Custom hook for course search functionality
 * Implements efficient search with debouncing and caching
 */
export const useCourseSearch = (initialFilters = {}) => {
  const { user } = useAuth();
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [filters, setFilters] = useState({
    is_active: "true", // Default to active courses for students
    ...initialFilters,
  });
  const [pagination, setPagination] = useState({
    currentPage: 1,
    totalPages: 1,
    totalCount: 0,
    pageSize: 20,
  });

  // Debounced search effect
  useEffect(() => {
    const timeoutId = setTimeout(() => {
      if (searchTerm || Object.keys(filters).some((key) => filters[key])) {
        performSearch();
      } else {
        fetchCourses();
      }
    }, 300); // 300ms debounce

    return () => clearTimeout(timeoutId);
  }, [searchTerm, filters, pagination.currentPage, pagination.pageSize]);

  // Main fetch function
  const fetchCourses = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);

      const params = {
        page: pagination.currentPage,
        page_size: pagination.pageSize,
      };

      // Add search term if present
      if (searchTerm.trim()) {
        params.search = searchTerm.trim();
      }

      // Add filters
      Object.keys(filters).forEach((key) => {
        if (filters[key] && filters[key] !== "") {
          params[key] = filters[key];
        }
      });

      console.log("🔍 Fetching courses with params:", params);

      const response = await coursesAPI.list(params);
      console.log("📊 Course search response:", response.data);

      const data = response.data;
      setCourses(data.results || data || []);

      // Update pagination
      if (data.count !== undefined) {
        setPagination((prev) => ({
          ...prev,
          totalCount: data.count,
          totalPages: Math.ceil(data.count / pagination.pageSize),
        }));
      }
    } catch (err) {
      console.error("❌ Error fetching courses:", err);
      setError(err.message || "Failed to fetch courses");
      setCourses([]);
    } finally {
      setLoading(false);
    }
  }, [searchTerm, filters, pagination.currentPage, pagination.pageSize]);

  // Perform search with same logic as fetch
  const performSearch = useCallback(fetchCourses, [fetchCourses]);

  // Update search term
  const updateSearchTerm = useCallback((term) => {
    setSearchTerm(term);
    setPagination((prev) => ({ ...prev, currentPage: 1 })); // Reset to first page on search
  }, []);

  // Update filters
  const updateFilters = useCallback((newFilters) => {
    setFilters((prev) => ({ ...prev, ...newFilters }));
    setPagination((prev) => ({ ...prev, currentPage: 1 })); // Reset to first page on filter change
  }, []);

  // Clear all filters and search
  const clearSearch = useCallback(() => {
    setSearchTerm("");
    setFilters({ is_active: user?.role === "student" ? "true" : "" });
    setPagination((prev) => ({ ...prev, currentPage: 1 }));
  }, [user]);

  // Go to specific page
  const goToPage = useCallback((page) => {
    setPagination((prev) => ({ ...prev, currentPage: page }));
  }, []);

  // Change page size
  const changePageSize = useCallback((size) => {
    setPagination((prev) => ({
      ...prev,
      pageSize: size,
      currentPage: 1, // Reset to first page when changing page size
    }));
  }, []);

  // Computed values
  const hasActiveSearch = useMemo(() => {
    return (
      searchTerm.trim() !== "" ||
      Object.keys(filters).some(
        (key) =>
          filters[key] &&
          filters[key] !== "" &&
          !(
            key === "is_active" &&
            filters[key] === "true" &&
            user?.role === "student"
          )
      )
    );
  }, [searchTerm, filters, user]);

  const isEmpty = useMemo(() => courses.length === 0, [courses]);

  const isFirstLoad = useMemo(
    () => pagination.currentPage === 1 && !hasActiveSearch && !loading,
    [pagination.currentPage, hasActiveSearch, loading]
  );

  return {
    // Data
    courses,
    loading,
    error,
    pagination,

    // Search state
    searchTerm,
    filters,
    hasActiveSearch,
    isEmpty,
    isFirstLoad,

    // Actions
    updateSearchTerm,
    updateFilters,
    clearSearch,
    goToPage,
    changePageSize,
    refresh: fetchCourses,

    // Computed helpers
    totalResults: pagination.totalCount,
    currentPageStart: (pagination.currentPage - 1) * pagination.pageSize + 1,
    currentPageEnd: Math.min(
      pagination.currentPage * pagination.pageSize,
      pagination.totalCount
    ),
  };
};

export default useCourseSearch;
