import React, { useState } from 'react';
import { Search, Filter, X, SlidersHorizontal, ChevronDown } from 'lucide-react';

/**
 * Advanced Search and Filter Component
 * Matches Django admin search functionality with additional features
 */
const SearchFilter = ({ 
  searchTerm, 
  onSearchChange, 
  filters, 
  onFiltersChange, 
  onClear,
  hasActiveSearch,
  loading = false,
  placeholder = "Search courses by name, code, description, or instructor...",
  showAdvancedFilters = true,
  className = ""
}) => {
  const [showFilters, setShowFilters] = useState(false);
  const [localSearchTerm, setLocalSearchTerm] = useState(searchTerm);

  // Handle search input with immediate local state update
  const handleSearchChange = (e) => {
    const value = e.target.value;
    setLocalSearchTerm(value);
    onSearchChange(value);
  };

  // Handle filter changes
  const handleFilterChange = (key, value) => {
    onFiltersChange({
      ...filters,
      [key]: value
    });
  };

  // Clear all filters and search
  const handleClear = () => {
    setLocalSearchTerm('');
    setShowFilters(false);
    onClear();
  };

  return (
    <div className={`bg-white rounded-2xl shadow-xl border border-gray-100 ${className}`}>
      <div className="p-6">
        {/* Main Search Bar */}
        <div className="flex flex-col lg:flex-row gap-4">
          {/* Search Input */}
          <div className="flex-1">
            <div className="relative group">
              <Search className={`absolute left-4 top-1/2 transform -translate-y-1/2 h-5 w-5 transition-colors duration-200 ${
                loading ? 'text-blue-500 animate-pulse' : 'text-gray-400 group-focus-within:text-blue-500'
              }`} />
              <input
                type="text"
                placeholder={placeholder}
                value={localSearchTerm}
                onChange={handleSearchChange}
                disabled={loading}
                className="w-full pl-12 pr-4 py-4 border border-gray-200 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200 text-gray-900 placeholder-gray-500 shadow-sm disabled:opacity-50 disabled:cursor-not-allowed"
              />
              {loading && (
                <div className="absolute right-4 top-1/2 transform -translate-y-1/2">
                  <div className="animate-spin rounded-full h-5 w-5 border-2 border-blue-500 border-t-transparent"></div>
                </div>
              )}
            </div>
          </div>

          {/* Filter Controls */}
          <div className="flex items-center space-x-3">
            {/* Quick Status Filter */}
            <div className="flex items-center space-x-2 bg-gray-50 rounded-xl p-2">
              <Filter className="h-5 w-5 text-gray-500" />
              <select
                value={filters.is_active || ''}
                onChange={(e) => handleFilterChange('is_active', e.target.value)}
                disabled={loading}
                className="bg-transparent border-none focus:ring-0 text-gray-700 font-medium cursor-pointer disabled:opacity-50"
              >
                <option value="">All Courses</option>
                <option value="true">Available Courses</option>
                <option value="false">Unavailable Courses</option>
              </select>
            </div>

            {/* Advanced Filters Toggle */}
            {showAdvancedFilters && (
              <button
                onClick={() => setShowFilters(!showFilters)}
                disabled={loading}
                className={`flex items-center space-x-2 px-4 py-2 rounded-xl transition-all duration-200 font-medium disabled:opacity-50 ${
                  showFilters
                    ? 'bg-blue-100 text-blue-700 border border-blue-200'
                    : 'bg-gray-100 hover:bg-gray-200 text-gray-700 border border-gray-200'
                }`}
              >
                <SlidersHorizontal className="h-4 w-4" />
                <span>Filters</span>
                <ChevronDown className={`h-4 w-4 transition-transform duration-200 ${showFilters ? 'rotate-180' : ''}`} />
              </button>
            )}

            {/* Clear Button */}
            {hasActiveSearch && (
              <button
                onClick={handleClear}
                disabled={loading}
                className="flex items-center space-x-2 px-4 py-2 text-gray-600 hover:text-gray-800 border border-gray-200 rounded-xl hover:bg-gray-50 transition-all duration-200 group disabled:opacity-50"
              >
                <X className="h-4 w-4 group-hover:rotate-90 transition-transform duration-200" />
                <span className="font-medium">Clear</span>
              </button>
            )}
          </div>
        </div>

        {/* Advanced Filters Panel */}
        {showFilters && showAdvancedFilters && (
          <div className="mt-6 pt-6 border-t border-gray-200">
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              
              {/* Credits Filter */}
              <div className="space-y-2">
                <label className="block text-sm font-medium text-gray-700">
                  Credits
                </label>
                <select
                  value={filters.credits || ''}
                  onChange={(e) => handleFilterChange('credits', e.target.value)}
                  disabled={loading}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent text-sm disabled:opacity-50"
                >
                  <option value="">Any Credits</option>
                  <option value="1">1 Credit</option>
                  <option value="2">2 Credits</option>
                  <option value="3">3 Credits</option>
                  <option value="4">4 Credits</option>
                  <option value="5">5 Credits</option>
                  <option value="6">6+ Credits</option>
                </select>
              </div>

              {/* Duration Filter */}
              <div className="space-y-2">
                <label className="block text-sm font-medium text-gray-700">
                  Duration (Months)
                </label>
                <select
                  value={filters.course_duration || ''}
                  onChange={(e) => handleFilterChange('course_duration', e.target.value)}
                  disabled={loading}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent text-sm disabled:opacity-50"
                >
                  <option value="">Any Duration</option>
                  <option value="1">1 Month</option>
                  <option value="2">2 Months</option>
                  <option value="3">3 Months</option>
                  <option value="4">4 Months</option>
                  <option value="6">6 Months</option>
                  <option value="12">12 Months</option>
                </select>
              </div>

              {/* Sort By */}
              <div className="space-y-2">
                <label className="block text-sm font-medium text-gray-700">
                  Sort By
                </label>
                <select
                  value={filters.ordering || ''}
                  onChange={(e) => handleFilterChange('ordering', e.target.value)}
                  disabled={loading}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent text-sm disabled:opacity-50"
                >
                  <option value="">Default (Name)</option>
                  <option value="course_name">Name (A-Z)</option>
                  <option value="-course_name">Name (Z-A)</option>
                  <option value="course_code">Code (A-Z)</option>
                  <option value="-course_code">Code (Z-A)</option>
                  <option value="credits">Credits (Low-High)</option>
                  <option value="-credits">Credits (High-Low)</option>
                  <option value="course_duration">Duration (Short-Long)</option>
                  <option value="-course_duration">Duration (Long-Short)</option>
                  <option value="-created_at">Newest First</option>
                  <option value="created_at">Oldest First</option>
                </select>
              </div>

            </div>

            {/* Filter Summary */}
            <div className="mt-4 flex flex-wrap gap-2">
              {Object.entries(filters).map(([key, value]) => {
                if (!value || (key === 'is_active' && value === 'true')) return null;
                
                const getFilterLabel = (key, value) => {
                  switch(key) {
                    case 'is_active': return value === 'false' ? 'Unavailable' : 'Available';
                    case 'credits': return `${value} Credits`;
                    case 'course_duration': return `${value} Months`;
                    case 'ordering': {
                      const orderingLabels = {
                        'course_name': 'Name (A-Z)',
                        '-course_name': 'Name (Z-A)',
                        'course_code': 'Code (A-Z)',
                        '-course_code': 'Code (Z-A)',
                        'credits': 'Credits ↑',
                        '-credits': 'Credits ↓',
                        'course_duration': 'Duration ↑',
                        '-course_duration': 'Duration ↓',
                        '-created_at': 'Newest',
                        'created_at': 'Oldest'
                      };
                      return `Sort: ${orderingLabels[value] || value}`;
                    }
                    default: return `${key}: ${value}`;
                  }
                };

                return (
                  <span
                    key={key}
                    className="inline-flex items-center space-x-1 px-3 py-1 bg-blue-100 text-blue-800 text-sm rounded-full"
                  >
                    <span>{getFilterLabel(key, value)}</span>
                    <button
                      onClick={() => handleFilterChange(key, '')}
                      className="ml-1 hover:text-blue-900 transition-colors"
                    >
                      <X className="h-3 w-3" />
                    </button>
                  </span>
                );
              })}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default SearchFilter;
