import React, { useState } from "react";
import { useAuth } from "../context/AuthContext";
import { coursesAPI, studentsAPI } from "../services/api";
import { useCourseSearch } from "../hooks/useCourseSearch";
import SearchFilter from "../components/SearchFilter";
import CourseCard from "../components/CourseCard";
import Pagination from "../components/Pagination";
import EmptyState from "../components/EmptyState";
import LoadingSpinner, { LoadingCard } from "../components/LoadingSpinner";
import { Plus, X } from "lucide-react";

const CoursesPage = () => {
  const { user } = useAuth();

  // Use the custom hook for search and data management
  const {
    courses,
    loading,
    error,
    searchTerm,
    filters,
    pagination,
    handleSearchChange,
    handleFilterChange,
    handlePageChange,
    clearSearch,
    refreshCourses,
    hasResults,
    isEmpty,
    hasFilters,
  } = useCourseSearch();

  // Modal state
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [modalType, setModalType] = useState("create"); // 'create', 'edit', 'delete'
  const [modalLoading, setModalLoading] = useState(false);

  // Form state
  const [formData, setFormData] = useState({
    title: "",
    course_code: "",
    description: "",
    credits: "",
    start_date: "",
    end_date: "",
    is_active: true,
    instructor: "",
  });

  const [availableInstructors, setAvailableInstructors] = useState([]);

  // Modal handlers
  const openCreateModal = () => {
    setFormData({
      title: "",
      course_code: "",
      description: "",
      credits: "",
      start_date: "",
      end_date: "",
      is_active: true,
      instructor: "",
    });
    setModalType("create");
    setShowModal(true);
    fetchInstructors();
  };

  const openEditModal = (course) => {
    setSelectedCourse(course);
    setFormData({
      title: course.title || "",
      course_code: course.course_code || "",
      description: course.description || "",
      credits: course.credits || "",
      start_date: course.start_date || "",
      end_date: course.end_date || "",
      is_active: course.is_active,
      instructor: course.instructor?.id || "",
    });
    setModalType("edit");
    setShowModal(true);
    fetchInstructors();
  };

  const openDeleteModal = (course) => {
    setSelectedCourse(course);
    setModalType("delete");
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setSelectedCourse(null);
    setModalLoading(false);
  };

  // Fetch instructors for dropdowns
  const fetchInstructors = async () => {
    try {
      const response = await studentsAPI.list({ role: "instructor" });
      setAvailableInstructors(response.data.results || []);
    } catch (error) {
      console.error("Error fetching instructors:", error);
    }
  };

  // Form handlers
  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setModalLoading(true);

    try {
      const submitData = {
        ...formData,
        credits: formData.credits ? parseInt(formData.credits) : 0,
        instructor: formData.instructor || null,
      };

      if (modalType === "create") {
        await coursesAPI.create(submitData);
      } else if (modalType === "edit") {
        await coursesAPI.update(selectedCourse.id, submitData);
      }

      refreshCourses(); // Refresh data using the hook
      closeModal();
    } catch (error) {
      console.error("Error saving course:", error);
      alert("Error saving course. Please try again.");
    } finally {
      setModalLoading(false);
    }
  };

  const handleDelete = async () => {
    setModalLoading(true);
    try {
      await coursesAPI.delete(selectedCourse.id);
      refreshCourses(); // Refresh data using the hook
      closeModal();
    } catch (error) {
      console.error("Error deleting course:", error);
      alert("Error deleting course. Please try again.");
    } finally {
      setModalLoading(false);
    }
  };

  // Render loading state
  if (loading && !hasResults) {
    return (
      <div className="min-h-screen bg-gray-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="mb-8">
            <h1 className="text-3xl font-bold text-gray-900">Courses</h1>
            <p className="mt-2 text-gray-600">
              Manage and organize your courses
            </p>
          </div>

          {/* Loading Cards */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {Array.from({ length: 6 }).map((_, index) => (
              <LoadingCard key={index} />
            ))}
          </div>
        </div>
      </div>
    );
  }

  // Render error state
  if (error) {
    return (
      <div className="min-h-screen bg-gray-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="bg-red-50 border border-red-200 rounded-lg p-6">
            <div className="flex items-center">
              <X className="h-5 w-5 text-red-400 mr-2" />
              <h3 className="text-lg font-medium text-red-800">
                Error Loading Courses
              </h3>
            </div>
            <p className="mt-2 text-red-700">{error}</p>
            <button
              onClick={refreshCourses}
              className="mt-4 bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700"
            >
              Try Again
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-8 space-y-4 sm:space-y-0">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Courses</h1>
            <p className="mt-2 text-gray-600">
              {pagination.totalCount > 0
                ? `${pagination.totalCount} total courses`
                : "Manage and organize your courses"}
            </p>
          </div>

          {user?.is_staff && (
            <button
              onClick={openCreateModal}
              className="inline-flex items-center px-4 py-2 border border-transparent rounded-lg shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            >
              <Plus className="h-4 w-4 mr-2" />
              Add Course
            </button>
          )}
        </div>

        {/* Search and Filters */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 mb-8">
          <SearchFilter
            searchTerm={searchTerm}
            onSearchChange={handleSearchChange}
            filters={filters}
            onFilterChange={handleFilterChange}
            onClearSearch={clearSearch}
            hasFilters={hasFilters}
            loading={loading}
            placeholder="Search courses by title, code, or description..."
          />
        </div>

        {/* Loading indicator for search */}
        {loading && hasResults && (
          <div className="mb-4">
            <LoadingSpinner size="small" text="Searching..." />
          </div>
        )}

        {/* Results */}
        {isEmpty ? (
          <EmptyState
            type={hasFilters ? "no-results" : "no-data"}
            hasFilters={hasFilters}
            onClearFilters={clearSearch}
            actionLabel={user?.is_staff ? "Create Course" : undefined}
            onAction={user?.is_staff ? openCreateModal : undefined}
          />
        ) : (
          <>
            {/* Courses Grid */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-8">
              {courses.map((course) => (
                <CourseCard
                  key={course.id}
                  course={course}
                  onEdit={openEditModal}
                  onDelete={openDeleteModal}
                  canEdit={user?.is_staff}
                />
              ))}
            </div>

            {/* Pagination */}
            <Pagination
              currentPage={pagination.currentPage}
              totalPages={pagination.totalPages}
              totalCount={pagination.totalCount}
              onPageChange={handlePageChange}
              loading={loading}
            />
          </>
        )}

        {/* Modal */}
        {showModal && (
          <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
            <div className="relative top-20 mx-auto p-5 border w-11/12 md:w-3/4 lg:w-1/2 shadow-lg rounded-md bg-white">
              {/* Modal Header */}
              <div className="flex items-center justify-between pb-4 border-b">
                <h3 className="text-xl font-semibold text-gray-900">
                  {modalType === "create" && "Create New Course"}
                  {modalType === "edit" && "Edit Course"}
                  {modalType === "delete" && "Delete Course"}
                </h3>
                <button
                  onClick={closeModal}
                  disabled={modalLoading}
                  className="text-gray-400 hover:text-gray-600 disabled:opacity-50"
                >
                  <X className="h-6 w-6" />
                </button>
              </div>

              {/* Modal Content */}
              <div className="mt-4">
                {modalType === "delete" ? (
                  // Delete Confirmation
                  <div>
                    <p className="text-gray-700 mb-4">
                      Are you sure you want to delete the course "
                      {selectedCourse?.title}"? This action cannot be undone.
                    </p>
                    <div className="flex justify-end space-x-3">
                      <button
                        onClick={closeModal}
                        disabled={modalLoading}
                        className="px-4 py-2 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50 disabled:opacity-50"
                      >
                        Cancel
                      </button>
                      <button
                        onClick={handleDelete}
                        disabled={modalLoading}
                        className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 disabled:opacity-50"
                      >
                        {modalLoading ? "Deleting..." : "Delete"}
                      </button>
                    </div>
                  </div>
                ) : (
                  // Create/Edit Form
                  <form onSubmit={handleSubmit} className="space-y-4">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      {/* Course Title */}
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          Course Title *
                        </label>
                        <input
                          type="text"
                          name="title"
                          value={formData.title}
                          onChange={handleInputChange}
                          required
                          disabled={modalLoading}
                          className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent disabled:opacity-50"
                          placeholder="Enter course title"
                        />
                      </div>

                      {/* Course Code */}
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          Course Code *
                        </label>
                        <input
                          type="text"
                          name="course_code"
                          value={formData.course_code}
                          onChange={handleInputChange}
                          required
                          disabled={modalLoading}
                          className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent disabled:opacity-50"
                          placeholder="e.g., CS101"
                        />
                      </div>

                      {/* Credits */}
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          Credits
                        </label>
                        <input
                          type="number"
                          name="credits"
                          value={formData.credits}
                          onChange={handleInputChange}
                          min="0"
                          disabled={modalLoading}
                          className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent disabled:opacity-50"
                          placeholder="3"
                        />
                      </div>

                      {/* Instructor */}
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          Instructor
                        </label>
                        <select
                          name="instructor"
                          value={formData.instructor}
                          onChange={handleInputChange}
                          disabled={modalLoading}
                          className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent disabled:opacity-50"
                        >
                          <option value="">Select Instructor</option>
                          {availableInstructors.map((instructor) => (
                            <option key={instructor.id} value={instructor.id}>
                              {instructor.first_name} {instructor.last_name}
                            </option>
                          ))}
                        </select>
                      </div>

                      {/* Start Date */}
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          Start Date
                        </label>
                        <input
                          type="date"
                          name="start_date"
                          value={formData.start_date}
                          onChange={handleInputChange}
                          disabled={modalLoading}
                          className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent disabled:opacity-50"
                        />
                      </div>

                      {/* End Date */}
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          End Date
                        </label>
                        <input
                          type="date"
                          name="end_date"
                          value={formData.end_date}
                          onChange={handleInputChange}
                          disabled={modalLoading}
                          className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent disabled:opacity-50"
                        />
                      </div>
                    </div>

                    {/* Description */}
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Description
                      </label>
                      <textarea
                        name="description"
                        value={formData.description}
                        onChange={handleInputChange}
                        rows={4}
                        disabled={modalLoading}
                        className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent disabled:opacity-50"
                        placeholder="Enter course description..."
                      />
                    </div>

                    {/* Active Status */}
                    <div className="flex items-center">
                      <input
                        type="checkbox"
                        name="is_active"
                        checked={formData.is_active}
                        onChange={handleInputChange}
                        disabled={modalLoading}
                        className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded disabled:opacity-50"
                      />
                      <label className="ml-2 block text-sm text-gray-900">
                        Course is active
                      </label>
                    </div>

                    {/* Form Actions */}
                    <div className="flex justify-end space-x-3 pt-4 border-t">
                      <button
                        type="button"
                        onClick={closeModal}
                        disabled={modalLoading}
                        className="px-4 py-2 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50 disabled:opacity-50"
                      >
                        Cancel
                      </button>
                      <button
                        type="submit"
                        disabled={modalLoading}
                        className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50"
                      >
                        {modalLoading
                          ? modalType === "create"
                            ? "Creating..."
                            : "Updating..."
                          : modalType === "create"
                          ? "Create Course"
                          : "Update Course"}
                      </button>
                    </div>
                  </form>
                )}
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default CoursesPage;
