import React from "react";
import { Loader2 } from "lucide-react";

/**
 * Reusable LoadingSpinner component
 * Displays loading states with consistent styling
 */
const LoadingSpinner = ({
  size = "medium",
  text = "Loading...",
  showText = true,
  className = "",
}) => {
  const sizeClasses = {
    small: "h-4 w-4",
    medium: "h-6 w-6",
    large: "h-8 w-8",
    xl: "h-12 w-12",
  };

  const textSizeClasses = {
    small: "text-sm",
    medium: "text-base",
    large: "text-lg",
    xl: "text-xl",
  };

  return (
    <div className={`flex items-center justify-center space-x-2 ${className}`}>
      <Loader2 className={`${sizeClasses[size]} animate-spin text-blue-600`} />
      {showText && (
        <span className={`${textSizeClasses[size]} text-gray-600 font-medium`}>
          {text}
        </span>
      )}
    </div>
  );
};

/**
 * Full page loading overlay
 */
export const LoadingOverlay = ({ text = "Loading...", className = "" }) => {
  return (
    <div
      className={`fixed inset-0 bg-white bg-opacity-75 flex items-center justify-center z-50 ${className}`}
    >
      <LoadingSpinner size="xl" text={text} />
    </div>
  );
};

/**
 * Card/section loading state
 */
export const LoadingCard = ({ className = "" }) => {
  return (
    <div
      className={`bg-white rounded-lg shadow-md border border-gray-200 p-6 ${className}`}
    >
      <div className="animate-pulse">
        <div className="flex items-center space-x-3 mb-4">
          <div className="h-5 w-5 bg-gray-300 rounded"></div>
          <div className="h-4 bg-gray-300 rounded w-3/4"></div>
        </div>
        <div className="space-y-3">
          <div className="h-3 bg-gray-300 rounded w-full"></div>
          <div className="h-3 bg-gray-300 rounded w-5/6"></div>
          <div className="h-3 bg-gray-300 rounded w-4/6"></div>
        </div>
        <div className="grid grid-cols-2 gap-4 mt-6">
          <div className="h-3 bg-gray-300 rounded"></div>
          <div className="h-3 bg-gray-300 rounded"></div>
        </div>
      </div>
    </div>
  );
};

/**
 * Table/list loading state
 */
export const LoadingList = ({ rows = 5, className = "" }) => {
  return (
    <div className={`space-y-4 ${className}`}>
      {Array.from({ length: rows }).map((_, index) => (
        <div
          key={index}
          className="bg-white rounded-lg shadow-md border border-gray-200 p-4"
        >
          <div className="animate-pulse">
            <div className="flex items-center space-x-3">
              <div className="h-10 w-10 bg-gray-300 rounded-full"></div>
              <div className="flex-1 space-y-2">
                <div className="h-4 bg-gray-300 rounded w-3/4"></div>
                <div className="h-3 bg-gray-300 rounded w-1/2"></div>
              </div>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default LoadingSpinner;
